import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { MapPin, Phone, Edit, LogOut } from "lucide-react";
import { Truck, CheckCircle, XCircle, RefreshCw } from "lucide-react";


const Profile = () => {
        // profile
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;
        if (error) return <div className="text-center text-red-500">{error}</div>;

        // orders
        const [orders, setOrders] = useState([]);

        useEffect(() => {
                const fetchOrders = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        setError("User not logged in");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/orders/my-orders", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                console.log("✅ Orders Fetched:", data);
                                setOrders(data);
                        } catch (error) {
                                console.error("❌ Error Fetching Orders:", error.response?.data || error.message);
                                setError("Failed to load orders.");
                        } finally {
                                setLoading(false);
                        }
                };

                fetchOrders();
        }, []);

        const cancelOrder = async (orderId) => {
                setOrders(
                        orders.map((order) =>
                                order._id === orderId ? { ...order, orderStatus: "Cancelled" } : order
                        )
                );
                alert("Order cancelled successfully!");
        };

        const returnOrder = async (orderId) => {
                setOrders(
                        orders.map((order) =>
                                order._id === orderId ? { ...order, orderStatus: "Returned" } : order
                        )
                );
                alert("Order return requested!");
        };

        return (
                <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-[#F7E6D4] to-[#E3C6A4] px-6">

                        {/* Profile Container */}
                        <div className="w-full max-w-4xl bg-white/80 shadow-2xl rounded-2xl backdrop-blur-lg border border-[#C69C84] p-8 flex space-x-10">

                                {/* Left Side: Profile Picture Only */}
                                <div className="w-1/4 flex justify-center items-center">
                                        <img
                                                className="w-32 h-32 rounded-full border-4 border-[#D4A190] shadow-lg"
                                                src={`https://ui-avatars.com/api/?name=${user?.name}&background=9B6A62&color=fff`}
                                                alt="User Avatar"
                                        />
                                </div>

                                {/* Right Side: Profile Details & Actions */}
                                <div className="flex-1 space-y-6">

                                        {/* Name & Email (Upper Section) */}
                                        <div className="text-center">
                                                <h1 className="text-3xl font-semibold text-[#9B6A62]">{user?.name}</h1>
                                                <p className="text-gray-600 text-lg">{user?.email}</p>
                                        </div>

                                        {/* Contact Details (Middle Section) */}
                                        <div className="bg-white/60 rounded-lg p-6 shadow-md border border-[#D4A190] space-y-4">
                                                <div className="flex items-center gap-4 text-gray-700">
                                                        <Phone className="text-[#9B6A62] w-6 h-6" />
                                                        <p className="text-lg"><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                                </div>
                                                <div className="flex items-center gap-4 text-gray-700">
                                                        <MapPin className="text-[#9B6A62] w-6 h-6" />
                                                        <p className="text-lg"><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                                </div>
                                        </div>

                                        {/* Action Buttons (Bottom Section) */}
                                        <div className="flex justify-between space-x-6">
                                                <button
                                                        onClick={() => navigate("/edit-profile")}
                                                        className="flex items-center justify-center gap-3 w-1/2 bg-gradient-to-r from-[#D4A190] to-[#9B6A62] text-white py-3 px-6 rounded-xl hover:scale-105 transition-all shadow-lg"
                                                >
                                                        <Edit className="w-6 h-6" /> Edit Profile
                                                </button>
                                                <button
                                                        onClick={handleLogout}
                                                        className="flex items-center justify-center gap-3 w-1/2 bg-gradient-to-r from-[#E87768] to-[#C04848] text-white py-3 px-6 rounded-xl hover:scale-105 transition-all shadow-lg"
                                                >
                                                        <LogOut className="w-6 h-6" /> Logout
                                                </button>
                                        </div>

                                </div>

                        </div>

                </div>



        );
};

export default Profile;
