import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Get product ID from URL
        const [product, setProduct] = useState(null);
        const [recommendedProducts, setRecommendedProducts] = useState([]);
        const [loading, setLoading] = useState(true);

        useEffect(() => {
                const fetchProduct = async () => {
                        try {
                                const response = await axios.get(`http://localhost:5000/api/product/${id}`);
                                setProduct(response.data); // Assuming the response has a product object
                                setLoading(false);
                        } catch (error) {
                                console.error("Error fetching product:", error);
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]); // Runs only when `id` changes

        // Fetch recommended products only when `product?.category?._id` is available
        useEffect(() => {
                if (!product?.category?._id) return;

                const fetchRecommendedProducts = async () => {
                        try {
                                const response = await axios.get(`http://localhost:5000/api/product/category/${product.category._id}`);
                                setRecommendedProducts(response.data);
                        } catch (error) {
                                console.error("Error fetching recommended products:", error);
                        }
                };

                fetchRecommendedProducts();
        }, [product?.category?._id]); // Depend only on `category._id`, not `id`

        if (loading) {
                return <div className="text-center text-gray-700 text-xl mt-10">Loading product details...</div>;
        }

        if (!product) {
                return <div className="text-center text-red-600 text-xl mt-10">No product found with this ID.</div>;
        }

        return (
                <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
                        <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-xl">
                                {/* Product Details */}
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                        {/* Product Image */}
                                        <div className="relative group">
                                                <img
                                                        src={product.images?.[0] || "/placeholder.jpg"} // Avoid breaking if `images` is undefined
                                                        alt={product.name}
                                                        className="w-full h-auto rounded-lg shadow-lg transition-transform duration-300 transform group-hover:scale-105"
                                                />
                                        </div>

                                        {/* Product Info */}
                                        <div>
                                                <h1 className="text-4xl font-semibold text-gray-800">{product.name}</h1>
                                                <p className="text-2xl text-amber-600 font-semibold mt-2">${product.price}</p>
                                                <p className="text-gray-700 mt-4">{product.description}</p>

                                                {/* Material and Craftsmanship Details */}
                                                <div className="mt-4 space-y-2">
                                                        <p className="text-gray-600">
                                                                <strong>Material:</strong> {product.material || "Not specified"}
                                                        </p>
                                                        <p className="text-gray-600">
                                                                <strong>Gemstone:</strong> {product.gemstone || "Not specified"}
                                                        </p>
                                                        <p className="text-gray-600">
                                                                <strong>Craftsmanship:</strong> {product.craftsmanship || "Not specified"}
                                                        </p>
                                                </div>

                                                {/* Size Selection */}
                                                <div className="mt-6">
                                                        <h3 className="font-medium text-lg">Select Size:</h3>
                                                        <div className="flex space-x-3 mt-2">
                                                                {product.sizes?.map((size) => (
                                                                        <button
                                                                                key={size}
                                                                                className="border px-4 py-2 rounded-md hover:bg-gray-200 transition text-gray-800 font-medium"
                                                                        >
                                                                                {size}
                                                                        </button>
                                                                ))}
                                                        </div>
                                                </div>

                                                {/* Action Buttons */}
                                                <div className="mt-6 flex space-x-4">
                                                        <button className="bg-amber-600 text-white px-6 py-2 rounded-md shadow hover:bg-amber-700 transition">
                                                                Add to Cart
                                                        </button>
                                                        <button className="bg-gray-900 text-white px-6 py-2 rounded-md shadow hover:bg-gray-800 transition">
                                                                Buy Now
                                                        </button>
                                                </div>

                                                {/* Product Category */}
                                                <p className="mt-6 text-gray-500">
                                                        <strong>Category:</strong> {product.category?.name || "Not specified"}
                                                </p>
                                        </div>
                                </div>

                                {/* Long Description */}
                                <div className="mt-12">
                                        <h2 className="text-2xl font-semibold text-gray-800">Product Details</h2>
                                        <p className="text-gray-600 mt-2 leading-relaxed">{product.longDescription || "No further details available."}</p>
                                </div>

                                {/* Recommended Products */}
                                <div className="mt-12">
                                        <h2 className="text-2xl font-semibold text-gray-800 mb-4">You May Also Like</h2>
                                        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                                                {recommendedProducts.length > 0 ? (
                                                        recommendedProducts.map((rec) => (
                                                                <div key={rec._id} className="bg-white p-4 rounded-lg shadow-lg transition-transform transform hover:scale-105">
                                                                        <img src={rec.images?.[0] || "/placeholder.jpg"} alt={rec.name} className="w-full h-40 object-cover rounded-md" />
                                                                        <h3 className="text-lg font-semibold mt-3 text-gray-800">{rec.name}</h3>
                                                                        <p className="text-amber-600 font-medium">${rec.price}</p>
                                                                        <button className="mt-3 w-full bg-gray-900 text-white py-2 rounded-md hover:bg-gray-800 transition">
                                                                                View Product
                                                                        </button>
                                                                </div>
                                                        ))
                                                ) : (
                                                        <p className="text-gray-500">No recommended products available.</p>
                                                )}
                                        </div>
                                </div>
                        </div>
                </div>
        );
};

export default SingleProduct;
