import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaFacebook, FaTwitter, FaInstagram } from "react-icons/fa";
import { FiUser } from "react-icons/fi";
import { AiOutlineShoppingCart } from "react-icons/ai";
import { BsBox } from "react-icons/bs";
import { Menu, X } from "lucide-react";

const Navbar = () => {
        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isOpen, setIsOpen] = useState(false);
        const [profileOpen, setProfileOpen] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);

        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token); // Convert token existence to boolean
                };

                checkAuth();
                window.addEventListener("storage", checkAuth); // Update on login/logout

                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        return (
                <nav className="bg-white shadow-md dark:bg-gray-900">
                        {/* Upper part with contact and social icons */}
                        <div className="flex justify-between items-center px-6 py-2 bg-gray-100 dark:bg-gray-800 text-sm">
                                <div className="text-gray-700 dark:text-gray-300">
                                        <span>📞 +123 456 7890 | ✉️ support@jewelstore.com</span>
                                </div>
                                <div className="flex space-x-4 text-gray-700 dark:text-gray-300">
                                        <Facebook className="w-5 h-5 cursor-pointer hover:text-blue-600" />
                                        <Twitter className="w-5 h-5 cursor-pointer hover:text-blue-400" />
                                        <Instagram className="w-5 h-5 cursor-pointer hover:text-pink-500" />
                                </div>
                        </div>

                        {/* Bottom part with navigation and icons */}
                        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                                <div className="flex justify-between items-center h-16">
                                        {/* Left - Navigation Links */}
                                        <div className="hidden md:flex space-x-6 text-gray-800 dark:text-gray-200">
                                                <Link to="/" className="hover:text-rose-500">Home</Link>
                                                <Link to="/contact" className="hover:text-rose-500">Contact</Link>
                                                <Link to="/products" className="hover:text-rose-500">Products</Link>
                                        </div>

                                        {/* Center - Logo */}
                                        <Link to="/" className="text-3xl font-bold text-rose-500">JewelStore</Link>

                                        {/* Right - Profile, Cart, and Orders (if logged in) */}
                                        <div className="relative flex items-center space-x-4">
                                                {isAuthenticated ? (
                                                        <>
                                                                {/* Profile Dropdown */}
                                                                <div className="relative">
                                                                        <button
                                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                                onMouseEnter={() => setIsDropdownOpen(true)}
                                                                                onMouseLeave={() => setIsDropdownOpen(false)}
                                                                                className="relative focus:outline-none"
                                                                        >
                                                                                <User className="w-6 h-6 text-gray-800 dark:text-gray-200 cursor-pointer" />
                                                                        </button>
                                                                        {isDropdownOpen && (
                                                                                <div
                                                                                        className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-800 shadow-lg rounded-md overflow-hidden z-50"
                                                                                        onMouseEnter={() => setIsDropdownOpen(true)}
                                                                                        onMouseLeave={() => setIsDropdownOpen(false)}
                                                                                >
                                                                                        <Link to="/profile" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Profile</Link>
                                                                                        <Link to="/wishlist" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Wishlist</Link>
                                                                                        <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-red-600 hover:bg-gray-100 dark:hover:bg-gray-700">
                                                                                                <LogOut className="w-4 h-4 inline-block mr-2" /> Logout
                                                                                        </button>
                                                                                </div>
                                                                        )}
                                                                </div>
                                                                <Link to="/cart">
                                                                        <ShoppingCart className="w-6 h-6 text-gray-800 dark:text-gray-200 hover:text-rose-500" />
                                                                </Link>
                                                                <Link to="/order-history">
                                                                        <Package className="w-6 h-6 text-gray-800 dark:text-gray-200 hover:text-rose-500" />
                                                                </Link>
                                                        </>
                                                ) : (
                                                        <Link to="/login" className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600">
                                                                Login
                                                        </Link>
                                                )}
                                        </div>
                                </div>
                        </div>
                </nav>
        );
};

export default Navbar;
