// Backend: productRoutes.js
const express = require('express');
const router = express.Router();
const { getProductById } = require('../controllers/productController');

// Make sure this route is registered in your main app.js/server.js
router.get('/products/:id', getProductById);

module.exports = router;

// Backend: productController.js
const Product = require('../models/Product');
const mongoose = require('mongoose');

exports.getProductById = async (req, res) => {
        try {
                const { id } = req.params;

                if (!mongoose.Types.ObjectId.isValid(id)) {
                        return res.status(400).json({
                                success: false,
                                message: "Invalid product ID format"
                        });
                }

                const product = await Product.findById(id)
                        .populate('category', 'name')
                        .lean();

                if (!product) {
                        return res.status(404).json({
                                success: false,
                                message: "Product not found"
                        });
                }

                res.status(200).json({
                        success: true,
                        product
                });
        } catch (error) {
                console.error('Error in getProductById:', error);
                res.status(500).json({
                        success: false,
                        message: "Error fetching product",
                        error: error.message
                });
        }
};

// Frontend: SingleProduct.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SingleProduct = () => {
        const { id } = useParams();
        const navigate = useNavigate();
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        useEffect(() => {
                const fetchProduct = async () => {
                        try {
                                setLoading(true);
                                setError(null);

                                // Make sure this URL matches your backend API URL
                                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/products/${id}`);

                                if (response.data.success) {
                                        setProduct(response.data.product);
                                } else {
                                        setError('Failed to fetch product details');
                                }
                        } catch (error) {
                                console.error('Error fetching product:', error);
                                setError(error.response?.data?.message || 'Error fetching product details');
                                if (error.response?.status === 404) {
                                        navigate('/not-found'); // Redirect to 404 page if product not found
                                }
                        } finally {
                                setLoading(false);
                        }
                };

                if (id) {
                        fetchProduct();
                }
        }, [id, navigate]);

        if (loading) {
                return (
                        <div className="min-h-screen flex items-center justify-center">
                                <div className="text-center text-gray-700 text-xl">
                                        Loading product details...
                                </div>
                        </div>
                );
        }

        if (error) {
                return (
                        <div className="min-h-screen flex items-center justify-center">
                                <div className="text-center text-red-600 text-xl">
                                        {error}
                                </div>
                        </div>
                );
        }

        if (!product) {
                return (
                        <div className="min-h-screen flex items-center justify-center">
                                <div className="text-center text-red-600 text-xl">
                                        Product not found
                                </div>
                        </div>
                );
        }

        return (
                <div className="min-h-screen bg-gray-100 p-6">
                        <div className="max-w-6xl mx-auto bg-white rounded-lg shadow-xl p-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                        {/* Product Image */}
                                        <div className="relative group">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full h-auto rounded-lg shadow-lg transition-transform duration-300 transform group-hover:scale-105"
                                                />
                                        </div>

                                        {/* Product Info */}
                                        <div>
                                                <h1 className="text-4xl font-semibold text-gray-800">
                                                        {product.name}
                                                </h1>
                                                <p className="text-2xl text-amber-600 font-semibold mt-2">
                                                        ${product.price}
                                                </p>
                                                <p className="text-gray-700 mt-4">
                                                        {product.description}
                                                </p>

                                                {/* Product Details */}
                                                {product.material && (
                                                        <div className="mt-4 space-y-2">
                                                                <p className="text-gray-600">
                                                                        <strong>Material:</strong> {product.material}
                                                                </p>
                                                                {product.gemstone && (
                                                                        <p className="text-gray-600">
                                                                                <strong>Gemstone:</strong> {product.gemstone}
                                                                        </p>
                                                                )}
                                                                {product.craftsmanship && (
                                                                        <p className="text-gray-600">
                                                                                <strong>Craftsmanship:</strong> {product.craftsmanship}
                                                                        </p>
                                                                )}
                                                        </div>
                                                )}

                                                {/* Size Selection */}
                                                {product.sizes && product.sizes.length > 0 && (
                                                        <div className="mt-6">
                                                                <h3 className="font-medium text-lg">Select Size:</h3>
                                                                <div className="flex flex-wrap gap-3 mt-2">
                                                                        {product.sizes.map((size) => (
                                                                                <button
                                                                                        key={size}
                                                                                        className="border px-4 py-2 rounded-md hover:bg-gray-200 transition text-gray-800 font-medium"
                                                                                >
                                                                                        {size}
                                                                                </button>
                                                                        ))}
                                                                </div>
                                                        </div>
                                                )}

                                                {/* Action Buttons */}
                                                <div className="mt-6 flex flex-wrap gap-4">
                                                        <button className="bg-amber-600 text-white px-6 py-2 rounded-md shadow hover:bg-amber-700 transition">
                                                                Add to Cart
                                                        </button>
                                                        <button className="bg-gray-900 text-white px-6 py-2 rounded-md shadow hover:bg-gray-800 transition">
                                                                Buy Now
                                                        </button>
                                                </div>

                                                {/* Category */}
                                                {product.category && (
                                                        <p className="mt-6 text-gray-500">
                                                                <strong>Category:</strong> {product.category.name}
                                                        </p>
                                                )}
                                        </div>
                                </div>

                                {/* Long Description */}
                                {product.longDescription && (
                                        <div className="mt-12">
                                                <h2 className="text-2xl font-semibold text-gray-800">
                                                        Product Details
                                                </h2>
                                                <p className="text-gray-600 mt-2 leading-relaxed">
                                                        {product.longDescription}
                                                </p>
                                        </div>
                                )}
                        </div>
                </div>
        );
};

export default SingleProduct;
