import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const [message, setMessage] = useState(""); // State for feedback messages


        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError(data.message || "Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        } finally {
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);


        // Function to add product to cart
        const handleAddToCart = async () => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("Please log in to add items to cart.");
                        return;
                }

                console.log("Sending request to add product:", { userId, productId: id });

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();
                        console.log("Response from server:", data);

                        if (response.ok) {
                                setMessage("Added to cart successfully!");
                        } else {
                                setMessage(data.message || "Error adding to cart");
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("Server error while adding to cart");
                }
        };

        const handleAddToWishlist = async () => {
                try {
                        await axios.post("http://localhost:5000/api/wishlist/add", { productId: product._id }, {
                                headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
                        });

                        alert("Added to Wishlist!");
                } catch (error) {
                        console.error("Error adding to wishlist:", error);
                }
        };


        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="container mx-auto px-6 py-16">
                        {product && (
                                <div className="bg-white shadow-lg rounded-lg overflow-hidden">
                                        {/* Product Info Section */}
                                        <div className="flex flex-col lg:flex-row items-center lg:items-start p-6">
                                                {/* Image Section */}
                                                <div className="flex-1 max-w-xs mx-auto lg:max-w-md mb-6 lg:mb-0">
                                                        <img
                                                                src={product.image}
                                                                alt={product.name}
                                                                className="w-full h-auto object-cover rounded-lg shadow-sm"
                                                        />
                                                </div>

                                                {/* Product Details */}
                                                <div className="flex-1 lg:ml-8 space-y-4">
                                                        <h2 className="text-3xl font-semibold text-gray-800">{product.name}</h2>
                                                        <p className="text-lg text-gray-600">{product.description}</p>
                                                        <div className="flex items-center space-x-4">
                                                                <span className="text-2xl font-bold text-blue-600">${product.price}</span>
                                                                <button onClick={handleAddToCart} className="mt-4 bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition duration-300">
                                                                        Add to Cart
                                                                </button>
                                                                <button onClick={handleAddToWishlist} className="mt-4 bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition duration-300">
                                                                        Add to Wishlist
                                                                </button>
                                                                {message && <p className="text-green-600 mt-2">{message}</p>}
                                                        </div>
                                                </div>
                                        </div>

                                        {/* Category Description Section */}
                                        <div className="px-6 py-10 bg-gray-100">
                                                <h3 className="text-2xl font-semibold text-gray-800">Category Description</h3>
                                                <p className="mt-4 text-gray-600">
                                                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed nec lorem at neque elementum auctor ac eget felis.
                                                </p>
                                        </div>

                                        {/* Related Products Section */}
                                        <div className="px-6 py-10">
                                                <h3 className="text-2xl font-semibold text-gray-800 mb-6">You May Also Like</h3>
                                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                                                        {/* Product Card 1 */}
                                                        <div className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
                                                                <img
                                                                        src="https://via.placeholder.com/300"
                                                                        alt="Related Product"
                                                                        className="w-full h-48 object-cover rounded-t-lg"
                                                                />
                                                                <div className="p-4">
                                                                        <h4 className="text-xl font-medium text-gray-800">Product Name</h4>
                                                                        <p className="text-gray-600 mt-2">$39.99</p>
                                                                        <button className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition duration-300">
                                                                                Add to Cart
                                                                        </button>
                                                                </div>
                                                        </div>

                                                        {/* Product Card 2 */}
                                                        <div className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
                                                                <img
                                                                        src="https://via.placeholder.com/300"
                                                                        alt="Related Product"
                                                                        className="w-full h-48 object-cover rounded-t-lg"
                                                                />
                                                                <div className="p-4">
                                                                        <h4 className="text-xl font-medium text-gray-800">Product Name</h4>
                                                                        <p className="text-gray-600 mt-2">$49.99</p>
                                                                        <button className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition duration-300">
                                                                                Add to Cart
                                                                        </button>
                                                                </div>
                                                        </div>

                                                        {/* Product Card 3 */}
                                                        <div className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300">
                                                                <img
                                                                        src="https://via.placeholder.com/300"
                                                                        alt="Related Product"
                                                                        className="w-full h-48 object-cover rounded-t-lg"
                                                                />
                                                                <div className="p-4">
                                                                        <h4 className="text-xl font-medium text-gray-800">Product Name</h4>
                                                                        <p className="text-gray-600 mt-2">$59.99</p>
                                                                        <button className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition duration-300">
                                                                                Add to Cart
                                                                        </button>
                                                                </div>
                                                        </div>
                                                </div>
                                        </div>
                                </div>
                        )}
                </div>
        );

};

export default SingleProduct;
