import React, { useState, useEffect } from "react";
import axios from "axios";
import { Truck, CheckCircle, XCircle, RefreshCw } from "lucide-react";

const OrderHistory = () => {
        

        

        return (
                <div className="min-h-screen bg-gray-100 p-8 flex justify-center">
                        <div className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-xl">

                                {/* Page Header */}
                                <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
                                        Order History
                                </h1>

                                {/* Loading State */}
                                {loading ? (
                                        <p className="text-center text-gray-600">Loading orders...</p>
                                ) : error ? (
                                        <p className="text-center text-red-500">{error}</p>
                                ) : orders.length > 0 ? (
                                        <div className="space-y-6">
                                                {orders.map((order) => (
                                                        <div
                                                                key={order._id}
                                                                className="flex items-center bg-gray-50 p-5 rounded-lg shadow-md transition border border-gray-200"
                                                        >
                                                                {/* Product Image (Placeholder for now) */}
                                                                <img
                                                                        src="https://via.placeholder.com/150"
                                                                        alt="Product"
                                                                        className="w-20 h-20 object-cover rounded-lg mr-6"
                                                                />

                                                                {/* Order Info */}
                                                                <div className="flex-1">
                                                                        <h2 className="text-lg font-semibold text-gray-800">
                                                                                {order.items.map((item) => item.name).join(", ")}
                                                                        </h2>
                                                                        <p className="text-gray-600">â‚¹{order.totalAmount.toFixed(2)}</p>
                                                                        <p
                                                                                className={`font-semibold mt-1 ${order.orderStatus === "Pending"
                                                                                                ? "text-yellow-500"
                                                                                                : order.orderStatus === "Confirmed"
                                                                                                        ? "text-green-500"
                                                                                                        : "text-red-500"
                                                                                        }`}
                                                                        >
                                                                                {order.orderStatus}
                                                                        </p>
                                                                </div>

                                                                {/* Action Buttons */}
                                                                {order.orderStatus === "Pending" && (
                                                                        <button
                                                                                onClick={() => cancelOrder(order._id)}
                                                                                className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                                                                        >
                                                                                <XCircle className="w-5 h-5 mr-1" />
                                                                                Cancel Order
                                                                        </button>
                                                                )}

                                                                {order.orderStatus === "Confirmed" && (
                                                                        <button
                                                                                onClick={() => returnOrder(order._id)}
                                                                                className="flex items-center bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-900 transition"
                                                                        >
                                                                                <RefreshCw className="w-5 h-5 mr-1" />
                                                                                Return Order
                                                                        </button>
                                                                )}
                                                        </div>
                                                ))}
                                        </div>
                                ) : (
                                        <div className="text-center py-12">
                                                <p className="text-lg text-gray-500">No orders found.</p>
                                        </div>
                                )}
                        </div>
                </div>
        );
};

export default OrderHistory;
