import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError("Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);

        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="container mx-auto p-6 bg-white shadow-lg rounded-lg">
                        {product && (
                                <div className="flex flex-col lg:flex-row items-center lg:items-start space-y-6 lg:space-y-0 lg:space-x-10">
                                        <div className="flex-1 max-w-sm mx-auto lg:max-w-md">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full h-auto object-cover rounded-lg shadow-lg"
                                                />
                                        </div>
                                        <div className="flex-1 pl-4 space-y-4">
                                                <h2 className="text-3xl font-semibold text-gray-800">{product.name}</h2>
                                                <p className="text-lg text-gray-600">{product.description}</p>
                                                <div className="mt-4 flex items-center space-x-4">
                                                        <span className="text-2xl font-bold text-blue-600">${product.price}</span>
                                                        <button className="mt-4 bg-blue-500 text-white py-3 px-8 rounded-xl hover:bg-blue-600 transition duration-300">
                                                                Add to Cart
                                                        </button>
                                                </div>
                                        </div>
                                </div>
                        )}
                </div>
        );
};

export default SingleProduct;
