import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const [message, setMessage] = useState(""); // State for feedback messages


        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError(data.message || "Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        } finally {
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);


        // Function to add product to cart
        const handleAddToCart = async () => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        hideMessage();
                        return;
                }

                console.log("Sending request to add product:", { userId, productId: id });

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();
                        console.log("Response from server:", data);

                        if (response.ok) {
                                setMessage("✅ Added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                hideMessage();
        };

        const handleAddToWishlist = async () => {
                const token = localStorage.getItem("userToken");

                if (!token) {
                        setMessage("⚠️ Please log in to add items to your wishlist.");
                        hideMessage();
                        return;
                }

                try {
                        const response = await axios.post(
                                `http://localhost:5000/api/wishlist/add/${id}`,
                                {},
                                {
                                        headers: { Authorization: `Bearer ${token}` },
                                }
                        );

                        console.log("Wishlist response:", response.data);
                        setMessage("❤️ Added to wishlist!");
                } catch (error) {
                        setMessage(`❌ ${error.response?.data?.message || "Failed to add to wishlist."}`);
                }

                hideMessage();
        };

        // Function to hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };



        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="container mx-auto px-6 py-16 bg-beige text-gray-900">
                        <div className="flex flex-col lg:flex-row items-start bg-white shadow-xl rounded-3xl p-8 space-y-8 lg:space-y-0 lg:space-x-12">
                                {/* Product Image Section */}
                                <div className="flex-1 max-w-lg mx-auto relative">
                                        <span className="absolute top-4 left-4 bg-roseGold-200 text-roseGold-700 text-xs font-semibold px-3 py-1 rounded-full shadow-md">Luxury Collection</span>
                                        <div className="bg-gradient-to-b from-roseGold-100 to-white shadow-lg rounded-3xl p-6">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full h-auto object-cover rounded-2xl shadow-md"
                                                />
                                        </div>
                                </div>

                                {/* Product Details Section */}
                                <div className="flex-1 text-gray-800 space-y-6">
                                        <h2 className="text-4xl font-bold text-gray-900 tracking-wide uppercase">{product.name}</h2>
                                        <p className="text-lg text-gray-700 leading-relaxed border-l-4 border-roseGold-400 pl-4">{product.description}</p>
                                        <div className="text-3xl font-extrabold text-roseGold-600">${product.price}</div>
                                        <div className="flex space-x-6 mt-6">
                                                <button onClick={handleAddToCart} className="bg-gradient-to-r from-roseGold-600 to-roseGold-500 text-white py-3 px-8 rounded-full shadow-lg hover:from-roseGold-700 hover:to-roseGold-600 transition duration-300">
                                                        Add to Cart
                                                </button>
                                                <button onClick={handleAddToWishlist} className="border border-roseGold-600 text-roseGold-600 py-3 px-8 rounded-full shadow-md hover:bg-roseGold-600 hover:text-white transition duration-300">
                                                        Add to Wishlist
                                                </button>
                                        </div>
                                        {message && <p className="text-green-600 font-medium mt-2">{message}</p>}
                                </div>
                        </div>

                        {/* Category Section */}
                        <div className="px-8 py-12 bg-roseGold-50 rounded-xl shadow-md mt-16 text-center">
                                <h3 className="text-2xl font-semibold text-gray-900">Category Description</h3>
                                <p className="mt-4 text-gray-700 leading-relaxed max-w-2xl mx-auto">
                                        Experience the epitome of craftsmanship with our exclusive collection, designed for timeless elegance and luxury.
                                </p>
                        </div>

                        {/* Related Products */}
                        <div className="px-8 py-12 mt-16">
                                <h3 className="text-2xl font-semibold text-gray-900 mb-8 text-center">You May Also Like</h3>
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
                                        {[1, 2, 3].map((_, index) => (
                                                <div key={index} className="bg-white rounded-3xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden">
                                                        <img
                                                                src="https://via.placeholder.com/300"
                                                                alt="Related Product"
                                                                className="w-full h-72 object-cover rounded-t-3xl"
                                                        />
                                                        <div className="p-6 text-center">
                                                                <h4 className="text-xl font-semibold text-gray-900">Product Name</h4>
                                                                <p className="text-gray-600 mt-2 text-lg font-medium">$49.99</p>
                                                                <button className="mt-4 bg-gradient-to-r from-roseGold-600 to-roseGold-500 text-white py-3 px-6 rounded-full hover:from-roseGold-700 hover:to-roseGold-600 transition duration-300">
                                                                        Add to Cart
                                                                </button>
                                                        </div>
                                                </div>
                                        ))}
                                </div>
                        </div>
                </div>
        );

};

export default SingleProduct;


