import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";


const CheckoutPage = () => {
        const location = useLocation();
        const navigate = useNavigate();

        const cartItems = location.state?.cartItems || [];
        const subtotal = location.state?.total || 0;
        const finalAmount = location.state?.finalAmount || subtotal; // Use final amount directly

        const [address, setAddress] = useState("");
        const [addressError, setAddressError] = useState(""); // New state for address validation
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const [user, setUser] = useState();
        const [discountCode, setDiscountCode] = useState("");
        const [discountAmount, setDiscountAmount] = useState(0);
        const [discountMessage, setDiscountMessage] = useState("");

        const finalPayable = finalAmount - discountAmount || finalAmount;
        const [dCode, setDCode] = useState("");
        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                                setAddress(data.address || "");
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        // Address validation function
        const validateAddress = (input) => {
                // Blocked words (you can add more)
                const blockedWords = ["fake", "test", "unknown", "n/a", "123", "####"];

                // Regex to allow letters, numbers, spaces, commas, and common symbols
                const validAddressPattern = /^[a-zA-Z0-9\s,.-]+$/;

                if (input.length < 10) {
                        setAddressError("Address must be at least 10 characters long.");
                        return false;
                }

                if (!validAddressPattern.test(input)) {
                        setAddressError("Address contains invalid characters.");
                        return false;
                }

                if (blockedWords.some((word) => input.toLowerCase().includes(word))) {
                        setAddressError("This address is not valid for shipping.");
                        return false;
                }

                setAddressError(""); // Clear error if valid
                return true;
        };

        const handleAddressChange = (e) => {
                const newAddress = e.target.value;
                setAddress(newAddress);
                validateAddress(newAddress); // Validate on change
        };

        const applyDiscount = async () => {
                console.log("Applying Discount:", discountCode, subtotal); // Debug Log

                try {
                        const { data } = await axios.post("http://localhost:5000/api/discounts/apply", {
                                code: discountCode,
                                totalAmount: subtotal,
                        });

                        console.log("Response:", data); // ‚úÖ Check if response comes back

                        setDiscountAmount(data.discountAmount);
                        setDCode(data.discountCode);
                        console.log("fetched data.code", data.discountCode, "dCode: ", dCode);
                        setDiscountMessage("Discount applied successfully!");
                } catch (error) {
                        console.error("Discount Error:", error.response?.data || error.message);
                        setDiscountMessage(error.response?.data?.message || "Invalid discount code.");
                }
        };

        const handlePayment = async (cartItems, finalPayable, user) => {
                console.log("üöÄ handlePayment called!");
                console.log("üîπ cartItems:", cartItems);
                console.log("üîπ finalAmount:", finalPayable);
                console.log("üîπ user:", user);

                if (!user || !user._id) {
                        console.error("‚ùå User is missing or invalid! Make sure you are logged in.");
                        alert("‚ùå User data is missing. Please log in again.");
                        return;
                }

                try {
                        const token = localStorage.getItem("userToken");
                        if (!token) {
                                console.error("‚ùå Authentication error: Token is missing.");
                                alert("‚ùå Authentication error: Please log in.");
                                return;
                        }

                        const amountInPaise = Math.round(finalPayable * 100);

                        const loadRazorpayScript = () =>
                                new Promise((resolve) => {
                                        if (document.getElementById("razorpay-script")) {
                                                resolve(true);
                                                return;
                                        }
                                        const script = document.createElement("script");
                                        script.id = "razorpay-script";
                                        script.src = "https://checkout.razorpay.com/v1/checkout.js";
                                        script.onload = () => resolve(true);
                                        script.onerror = () => resolve(false);
                                        document.body.appendChild(script);
                                });

                        const scriptLoaded = await loadRazorpayScript();
                        if (!scriptLoaded) {
                                console.error("‚ùå Failed to load Razorpay.");
                                alert("‚ùå Failed to load Razorpay. Please try again.");
                                return;
                        }

                        if (typeof window.Razorpay === "undefined") {
                                console.error("‚ùå Razorpay not available.");
                                alert("‚ùå Razorpay not available. Try again later.");
                                return;
                        }

                        const options = {
                                key: "rzp_test_1DP5mmOlF5G5ag",
                                amount: amountInPaise,
                                currency: "INR",
                                name: "ShopEase",
                                description: "Order Payment",
                                handler: async function (response) {
                                        console.log("‚úÖ Payment successful! Payment ID:", response.razorpay_payment_id);
                                        alert("‚úÖ Payment successful!");

                                        const orderData = {
                                                user: user._id,
                                                items: cartItems.map((item) => ({
                                                        product: item.productId || item._id,
                                                        name: item.name,
                                                        quantity: item.quantity || 1,
                                                        price: item.price,
                                                })),
                                                totalAmount: finalPayable,
                                                paymentId: response.razorpay_payment_id,
                                                discountCode: dCode || "NO_DISCOUNT", // ‚úÖ Ensure discount is applied
                                                paymentStatus: "Paid",
                                        };

                                        console.log("üîπ Sending Order Request:", orderData);

                                        try {
                                                const res = await axios.post(
                                                        "http://localhost:5000/api/orders/store-after-payment",
                                                        orderData,
                                                        {
                                                                headers: {
                                                                        Authorization: `Bearer ${token}`,
                                                                        "Content-Type": "application/json",
                                                                },
                                                        }
                                                );

                                                console.log("‚úÖ Order stored successfully:", res.data);
                                                alert("‚úÖ Order placed successfully!");


                                                try {
                                                        if (!user || !user._id) {
                                                                console.error("‚ùå No valid user found.");
                                                                alert("User not found!");
                                                                return;
                                                        }

                                                        let userId = user._id;
                                                        console.log("User in cart try block: ", userId);
                                                        console.log("üõí Sending request to clear cart for user:", userId);

                                                        await axios.delete(`http://localhost:5000/api/cart/clear/${user._id}`, {
                                                                headers: {
                                                                        Authorization: `Bearer ${token}`,
                                                                        "Content-Type": "application/json"
                                                                }
                                                        });

                                                        console.log("‚úÖ Cart cleared successfully:", response.data);
                                                        alert("‚úÖ Items removed from cart!");

                                                        navigate("/order-history");

                                                } catch (error) {
                                                        console.error("‚ùå Error clearing cart:", error);
                                                        alert(`‚ö†Ô∏è Error: ${error.response?.data?.message || "Cart was not cleared."}`);
                                                }


                                        } catch (error) {
                                                console.error("‚ùå Error storing order:", error);
                                                alert("‚ùå Error storing order: " + (error.response?.data?.message || error.message));
                                        }
                                },
                                prefill: {
                                        name: user?.name || "Test User",
                                        email: user?.email || "test@example.com",
                                },
                                theme: { color: "#3399cc" },

                                modal: {
                                        ondismiss: async function () {
                                                console.log("‚ùå Payment was closed by the user.");
                                                alert("‚ùå Payment was not completed. Storing as unpaid order.");

                                                const unpaidOrderData = {
                                                        user: user._id,
                                                        items: cartItems.map((item) => ({
                                                                product: item.productId || item._id,
                                                                name: item.name,
                                                                quantity: item.quantity || 1,
                                                                price: item.price,
                                                        })),
                                                        totalAmount: finalPayable,
                                                        paymentId: null,
                                                        paymentStatus: "Unpaid",
                                                };

                                                console.log("üîπ Storing Unpaid Order:", unpaidOrderData);

                                                try {
                                                        const res = await axios.post(
                                                                "http://localhost:5000/api/orders/store-after-payment",
                                                                unpaidOrderData,
                                                                {
                                                                        headers: {
                                                                                Authorization: `Bearer ${token}`,
                                                                                "Content-Type": "application/json",
                                                                        },
                                                                }
                                                        );

                                                        console.log("‚úÖ Unpaid order stored successfully:", res.data);
                                                        alert("‚úÖ Unpaid order has been stored.");
                                                } catch (error) {
                                                        console.error("‚ùå Error storing unpaid order:", error.response?.data || error.message);
                                                        alert("‚ùå Error storing unpaid order: " + (error.response?.data?.message || error.message));
                                                }
                                        },
                                },
                        };

                        const rzp = new window.Razorpay(options);
                        rzp.open();
                } catch (error) {
                        console.error("‚ùå Unexpected Error:", error);
                        alert("‚ùå Unexpected error occurred.");
                }
        };


        return (
                        <div className="max-w-3xl mx-auto p-8 bg-[#0A1A2F]/80 backdrop-blur-lg rounded-xl mt-10 border border-[#D4AF37]/20 shadow-2xl relative">
                                {/* Decorative Elements */}
                                <div className="absolute -top-20 left-1/3 w-48 h-48 bg-gradient-to-r from-[#D4AF37]/10 to-[#B76E79]/10 rounded-full blur-2xl"></div>

                                <h2 className="text-3xl font-bold mb-6 text-[#D4AF37] font-heading border-b border-[#D4AF37]/30 pb-4">Secure Checkout</h2>

                                {/* Order Summary Section */}
                                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-6 mb-8 border border-white/10">
                                        <h3 className="text-xl font-semibold mb-4 text-[#F5F5F5]">Your Order</h3>
                                        <div className="space-y-4">
                                                {cartItems.map((item) => (
                                                        <div key={item.id} className="flex justify-between items-center py-3 border-b border-[#D4AF37]/10">
                                                                <span className="text-[#F5F5F5]/90">{item.name} <span className="text-[#D4AF37]/80">(x{item.quantity})</span></span>
                                                                <span className="font-medium text-[#D4AF37]">‚Çπ{(item.price * item.quantity).toFixed(2)}</span>
                                                        </div>
                                                ))}

                                                <div className="space-y-3 pt-4">
                                                        <div className="flex justify-between text-[#F5F5F5]/80">
                                                                <span>Subtotal:</span>
                                                                <span className="text-[#D4AF37]">‚Çπ{subtotal.toFixed(2)}</span>
                                                        </div>
                                                        <div className="flex justify-between text-[#F5F5F5]/80">
                                                                <span>Tax & Shipping:</span>
                                                                <span className="text-emerald-400">Included</span>
                                                        </div>
                                                </div>
                                        </div>
                                </div>

                                {/* Discount Section */}
                                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-6 mb-8 border border-white/10">
                                        <h3 className="text-xl font-semibold mb-4 text-[#F5F5F5]">Apply Discount</h3>
                                        <div className="flex gap-4">
                                                <input
                                                        type="text"
                                                        className="w-full bg-[#0A1A2F]/40 border border-[#D4AF37]/30 rounded-lg px-4 py-3 text-[#F5F5F5] placeholder-[#D4AF37]/50 focus:ring-2 focus:ring-[#D4AF37] focus:outline-none"
                                                        placeholder="Enter promo code"
                                                        value={discountCode}
                                                        onChange={(e) => setDiscountCode(e.target.value)}
                                                />
                                                <button
                                                        onClick={applyDiscount}
                                                        className="bg-gradient-to-r from-[#D4AF37] to-[#B76E79] hover:from-[#B76E79] hover:to-[#D4AF37] text-[#0A1A2F] px-6 py-3 rounded-lg font-semibold transition-all duration-300 whitespace-nowrap"
                                                >
                                                        Apply Code
                                                </button>
                                        </div>
                                        {discountMessage && <p className={`mt-3 text-sm ${discountMessage.includes('Applied') ? 'text-emerald-400' : 'text-red-400'}`}>{discountMessage}</p>}

                                        <div className="flex justify-between mt-4 pt-4 border-t border-[#D4AF37]/10">
                                                <span className="text-[#F5F5F5]/80">Discount Applied:</span>
                                                <span className="text-red-400">-‚Çπ{discountAmount.toFixed(2)}</span>
                                        </div>
                                </div>

                                {/* Final Amount */}
                                <div className="flex justify-between items-center mb-8 p-4 bg-[#D4AF37]/10 rounded-lg">
                                        <span className="text-xl font-semibold text-[#F5F5F5]">Total Payable:</span>
                                        <span className="text-2xl font-bold text-[#D4AF37]">‚Çπ{finalPayable.toFixed(2)}</span>
                                </div>

                                {/* Shipping Address */}
                                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-6 mb-8 border border-white/10">
                                        <h3 className="text-xl font-semibold mb-4 text-[#F5F5F5]">Shipping Details</h3>
                                        <textarea
                                                className="w-full bg-[#0A1A2F]/40 border border-[#D4AF37]/30 rounded-lg px-4 py-3 text-[#F5F5F5] placeholder-[#D4AF37]/50 focus:ring-2 focus:ring-[#D4AF37] focus:outline-none min-h-[120px]"
                                                value={address}
                                                onChange={handleAddressChange}
                                                placeholder="Enter your shipping address..."
                                        />
                                        {addressError && <p className="text-red-400 mt-2 text-sm">{addressError}</p>}
                                </div>

                                {/* Payment Button */}
                                <button
                                        onClick={() => handlePayment(cartItems, finalPayable, user)}
                                        className={`w-full py-4 rounded-xl font-semibold text-lg transition-all duration-300 ${cartItems.length === 0
                                                        ? "bg-gray-400/30 cursor-not-allowed text-gray-400"
                                                        : "bg-gradient-to-r from-[#D4AF37] to-[#B76E79] hover:from-[#B76E79] hover:to-[#D4AF37] text-[#0A1A2F] shadow-lg hover:shadow-xl"
                                                }`}
                                        disabled={cartItems.length === 0}
                                >
                                        {cartItems.length > 0
                                                ? `Secure Payment - ‚Çπ${finalPayable.toFixed(2)}`
                                                : "Add Items to Continue"}
                                </button>
                        </div>
        );
};


export default CheckoutPage;
