import { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const ResetPassword = () => {
        const { token } = useParams();
        const navigate = useNavigate();
        const [password, setPassword] = useState("");
        const [confirmPassword, setConfirmPassword] = useState("");
        const [message, setMessage] = useState("");
        const [error, setError] = useState("");

        const handleSubmit = async (e) => {
                e.preventDefault();
                setMessage("");
                setError("");

                if (password !== confirmPassword) {
                        setError("Passwords do not match!");
                        return;
                }

                try {
                        const { data } = await axios.post(`http://localhost:5000/api/users/reset-password/${token}`, { password });
                        setMessage(data.message);
                        setTimeout(() => navigate("/login"), 2000);
                } catch (err) {
                        setError(err.response?.data?.message || "Error resetting password.");
                }
        };

        return (
                <div className="flex items-center justify-center min-h-screen bg-[#F5ECE3]">
                        <div className="w-full max-w-lg bg-white shadow-xl rounded-3xl p-8 border border-[#D8B6A4] flex flex-col items-center">
                                <h2 className="text-3xl font-bold text-[#B88E8D] mb-6">Reset Password</h2>
                                <p className="text-gray-600 text-center mb-6">
                                        Enter your new password below.
                                </p>
                                <form onSubmit={handleSubmit} className="w-full space-y-4">
                                        <div className="relative w-full">
                                                <input
                                                        type="password"
                                                        placeholder="Enter new password"
                                                        value={password}
                                                        onChange={(e) => setPassword(e.target.value)}
                                                        required
                                                        className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                />
                                                <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">
                                                        ðŸ”’
                                                </span>
                                        </div>
                                        <div className="relative w-full">
                                                <input
                                                        type="password"
                                                        placeholder="Confirm new password"
                                                        value={confirmPassword}
                                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                                        required
                                                        className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                />
                                                <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-[#B88E8D]">
                                                        ðŸ”‘
                                                </span>
                                        </div>
                                        <button
                                                type="submit"
                                                className="w-full bg-[#B88E8D] text-white font-semibold py-3 rounded-lg shadow-md hover:bg-[#A17A7A] transition duration-300"
                                        >
                                                Reset Password
                                        </button>
                                </form>
                                {message && <p className="mt-4 text-green-600">{message}</p>}
                                {error && <p className="mt-4 text-red-600">{error}</p>}
                                <div className="mt-6 text-sm">
                                        <a href="/login" className="text-[#B88E8D] hover:underline">Back to Login</a>
                                </div>
                        </div>
                </div>
        );
};

export default ResetPassword;
