import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { User, ShoppingCart, X, Heart, Menu } from "lucide-react";

const Navbar = () => {
        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
        const [scrolled, setScrolled] = useState(false);
        const navRef = useRef(null);

        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);

                const handleScroll = () => {
                        if (!navRef.current) return;
                        const threshold = navRef.current.offsetHeight;
                        setScrolled(window.scrollY > threshold);
                };

                window.addEventListener('scroll', handleScroll);
                return () => {
                        window.removeEventListener("storage", checkAuth);
                        window.removeEventListener('scroll', handleScroll);
                };
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (
                <nav
                        ref={navRef}
                        className={`w-full z-50 transition-all duration-500 ${scrolled
                                        ? 'fixed top-4 left-1/2 -translate-x-1/2 w-[90%] max-w-4xl rounded-xl shadow-lg border border-[#E0D6CC] bg-white/95 backdrop-blur-lg'
                                        : 'static bg-white border-b border-[#E0D6CC]'
                                }`}
                >
                        <div className="px-6 py-3">
                                <div className="flex items-center justify-between">
                                        {/* Logo */}
                                        <Link to="/" className="group">
                                                <img
                                                        src="/logo.png"
                                                        alt="JewelStore"
                                                        className={`h-12 transition-all duration-500 ${scrolled ? 'opacity-100' : 'opacity-90'}`}
                                                />
                                        </Link>

                                        {/* Desktop Navigation */}
                                        <div className="hidden md:flex items-center gap-8">
                                                <Link
                                                        to="/"
                                                        className="relative text-[#6B4E4D] font-medium pb-1 hover:text-[#B76E79] transition-colors after:content-[''] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-[2px] after:bg-[#B76E79] hover:after:w-full after:transition-all after:duration-300"
                                                >
                                                        Home
                                                </Link>
                                                <Link
                                                        to="/products"
                                                        className="relative text-[#6B4E4D] font-medium pb-1 hover:text-[#B76E79] transition-colors after:content-[''] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-[2px] after:bg-[#B76E79] hover:after:w-full after:transition-all after:duration-300"
                                                >
                                                        Collections
                                                </Link>
                                                <Link
                                                        to="/contact"
                                                        className="relative text-[#6B4E4D] font-medium pb-1 hover:text-[#B76E79] transition-colors after:content-[''] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-[2px] after:bg-[#B76E79] hover:after:w-full after:transition-all after:duration-300"
                                                >
                                                        Contact
                                                </Link>
                                        </div>

                                        {/* User Actions */}
                                        <div className="flex items-center gap-5">
                                                <div className="flex items-center gap-4">
                                                        <Link to="/wishlist" className="relative p-2">
                                                                <Heart className="w-6 h-6 text-[#6B4E4D] hover:text-[#B76E79] transition-colors" />
                                                                <span className="absolute -top-1 -right-1 bg-[#B76E79] text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                                                                        2
                                                                </span>
                                                        </Link>
                                                        <Link to="/cart" className="relative p-2">
                                                                <ShoppingCart className="w-6 h-6 text-[#6B4E4D] hover:text-[#B76E79] transition-colors" />
                                                                <span className="absolute -top-1 -right-1 bg-[#B76E79] text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                                                                        3
                                                                </span>
                                                        </Link>
                                                </div>

                                                <div className="relative">
                                                        <button
                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                className="p-2 hover:bg-[#B76E79]/10 rounded-full transition-colors"
                                                        >
                                                                <User className="w-6 h-6 text-[#6B4E4D] hover:text-[#B76E79]" />
                                                        </button>

                                                        {isDropdownOpen && (
                                                                <div className="absolute right-0 mt-3 w-48 bg-white shadow-xl rounded-lg border border-[#E0D6CC] py-2">
                                                                        <Link
                                                                                to="/profile"
                                                                                className="block px-4 py-2.5 text-[#6B4E4D] hover:bg-[#FAF6F0]"
                                                                        >
                                                                                Profile
                                                                        </Link>
                                                                        <button
                                                                                onClick={handleLogout}
                                                                                className="block w-full text-left px-4 py-2.5 text-red-500 hover:bg-[#FAF6F0]"
                                                                        >
                                                                                Logout
                                                                        </button>
                                                                </div>
                                                        )}
                                                </div>
                                        </div>

                                        {/* Mobile Menu Button */}
                                        <button
                                                className="md:hidden p-2 hover:bg-[#B76E79]/10 rounded-full"
                                                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                        >
                                                {isMobileMenuOpen ? (
                                                        <X className="w-6 h-6 text-[#6B4E4D]" />
                                                ) : (
                                                        <Menu className="w-6 h-6 text-[#6B4E4D]" />
                                                )}
                                        </button>
                                </div>
                        </div>

                        {/* Mobile Menu */}
                        <div className={`md:hidden overflow-hidden transition-all duration-300 ${isMobileMenuOpen ? "max-h-screen" : "max-h-0"
                                }`}>
                                <div className="px-6 py-4 border-t border-[#E0D6CC] space-y-4">
                                        <Link
                                                to="/"
                                                className="block py-2 text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                onClick={() => setIsMobileMenuOpen(false)}
                                        >
                                                Home
                                        </Link>
                                        <Link
                                                to="/products"
                                                className="block py-2 text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                onClick={() => setIsMobileMenuOpen(false)}
                                        >
                                                Collections
                                        </Link>
                                        <Link
                                                to="/contact"
                                                className="block py-2 text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                onClick={() => setIsMobileMenuOpen(false)}
                                        >
                                                Contact
                                        </Link>
                                </div>
                        </div>
                </nav>
        );
};

export default Navbar;
