import { useState } from "react";

const CheckoutPage = () => {
        const [address, setAddress] = useState("123 Main St, City, Country");

        // Static Cart Data (Replace with dynamic data from context or API)
        const cartItems = [
                { id: 1, name: "Product A", quantity: 2, price: 20 },
                { id: 2, name: "Product B", quantity: 1, price: 35 },
        ];

        // Calculate Total
        const totalAmount = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

        const handleProceed = () => {
                alert("Proceeding to Payment..."); // Replace with API call
        };

        return (
                <div className="max-w-4xl mx-auto p-8 bg-white shadow-xl rounded-lg mt-12 border border-gray-300">
                        <h2 className="text-3xl font-bold mb-6 text-gray-900 border-b pb-2">Checkout</h2>

                        {/* Cart Summary */}
                        <div className="border p-6 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-xl font-semibold mb-4 text-gray-800">Order Summary</h3>
                                <div className="space-y-4">
                                        {cartItems.map((item) => (
                                                <div key={item.id} className="flex justify-between border-b pb-2 text-gray-700">
                                                        <span className="font-medium">{item.name} (x{item.quantity})</span>
                                                        <span className="font-semibold">${item.price * item.quantity}</span>
                                                </div>
                                        ))}
                                </div>
                                <div className="flex justify-between font-bold mt-4 text-gray-900 text-lg">
                                        <span>Total:</span>
                                        <span>${totalAmount}</span>
                                </div>
                        </div>

                        {/* Address Section */}
                        <div className="border p-6 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-xl font-semibold mb-4 text-gray-800">Shipping Address</h3>
                                <textarea
                                        className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none text-gray-700 text-lg"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                />
                        </div>

                        {/* Proceed Button */}
                        <button
                                onClick={handleProceed}
                                className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition font-semibold text-lg shadow-md">
                                Proceed to Payment
                        </button>
                </div>
        );
};

export default CheckoutPage;
