import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { User, ShoppingCart, Package, LogOut, Menu, X, Heart } from "lucide-react";


const Navbar = () => {

        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);



        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);
                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (
                <nav
                        className={`fixed top-6 left-1/2 transform -translate-x-1/2 w-[90%] md:w-[80%] bg-white/95 backdrop-blur-xl shadow-2xl px-8 py-3 flex items-center justify-between z-50 border border-[#E0D6CC] transition-all duration-500 ${isMobileMenuOpen ? "rounded-t-[2.5rem] rounded-b-none" : "rounded-[2.5rem]"
                                }`}
                >
                        {/* Left Section - Logo & Mobile Menu */}
                        <div className="flex items-center gap-6">
                                <button
                                        className="md:hidden p-2 bg-[#B76E79]/10 hover:bg-[#B76E79]/20 rounded-xl transition-all"
                                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                >
                                        {isMobileMenuOpen ? (
                                                <X className="w-7 h-7 text-[#B76E79]" />
                                        ) : (
                                                <Menu className="w-7 h-7 text-[#B76E79]" />
                                        )}
                                </button>
                                <Link to="/" className="group">
                                        <img
                                                src="/logo.png"
                                                alt="JewelStore"
                                                className="h-14 transition-transform duration-300 group-hover:scale-105"
                                        />
                                </Link>
                        </div>

                        {/* Center Navigation - Desktop */}
                        <div className="hidden md:flex items-center gap-2 absolute left-1/2 -translate-x-1/2">
                                {['Home', 'Collections', 'Besoke', 'Contact'].map((item) => (
                                        <Link
                                                key={item}
                                                to={`/${item.toLowerCase()}`}
                                                className="px-6 py-2.5 text-[#6B4E4D] hover:text-[#B76E79] group relative transition-all"
                                        >
                                                <span className="relative z-10">{item}</span>
                                                <div className="absolute inset-0 bg-gradient-to-b from-[#B76E79]/10 to-transparent opacity-0 group-hover:opacity-100 rounded-2xl transition-opacity" />
                                        </Link>
                                ))}
                        </div>

                        {/* Right Section - User Actions */}
                        <div className="flex items-center gap-5">
                                {isAuthenticated ? (
                                        <>
                                                {/* Wishlist & Cart */}
                                                <div className="flex gap-4">
                                                        <Link
                                                                to="/wishlist"
                                                                className="p-2.5 relative hover:bg-[#B76E79]/10 rounded-xl transition-colors"
                                                        >
                                                                <Heart className="w-6 h-6 text-[#B76E79]" />
                                                                <span className="absolute -top-1 -right-1 bg-[#B76E79] text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                                                                        2
                                                                </span>
                                                        </Link>

                                                        <Link
                                                                to="/cart"
                                                                className="p-2.5 relative hover:bg-[#B76E79]/10 rounded-xl transition-colors"
                                                        >
                                                                <ShoppingBag className="w-6 h-6 text-[#B76E79]" />
                                                                <span className="absolute -top-1 -right-1 bg-[#B76E79] text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                                                                        3
                                                                </span>
                                                        </Link>
                                                </div>

                                                {/* Profile Dropdown */}
                                                <div className="relative">
                                                        <button
                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                className="flex items-center gap-2 p-2 pr-3 hover:bg-[#B76E79]/10 rounded-xl transition-colors"
                                                        >
                                                                <div className="relative">
                                                                        <img
                                                                                src="/user-avatar.png"
                                                                                className="w-9 h-9 rounded-full border-2 border-[#B76E79]/30"
                                                                                alt="Profile"
                                                                        />
                                                                        <div className="absolute bottom-0 right-0 w-2.5 h-2.5 bg-emerald-500 rounded-full border-2 border-white" />
                                                                </div>
                                                        </button>

                                                        {isDropdownOpen && (
                                                                <div className="absolute right-0 mt-3 w-56 bg-white/95 backdrop-blur-xl shadow-2xl rounded-2xl py-4 border border-[#E0D6CC]">
                                                                        <div className="px-4 py-2 border-b border-[#E0D6CC]">
                                                                                <p className="font-serif text-[#6B4E4D]">Emily Carter</p>
                                                                                <p className="text-sm text-[#6B4E4D]/80">emily@jewelstore.com</p>
                                                                        </div>
                                                                        <div className="space-y-2 pt-3">
                                                                                <Link
                                                                                        to="/profile"
                                                                                        className="flex items-center gap-3 px-4 py-2.5 hover:bg-[#FAF6F0]"
                                                                                >
                                                                                        <User className="w-5 h-5 text-[#B76E79]" />
                                                                                        Account Settings
                                                                                </Link>
                                                                                <Link
                                                                                        to="/orders"
                                                                                        className="flex items-center gap-3 px-4 py-2.5 hover:bg-[#FAF6F0]"
                                                                                >
                                                                                        <Package className="w-5 h-5 text-[#B76E79]" />
                                                                                        Order History
                                                                                </Link>
                                                                                <button
                                                                                        onClick={handleLogout}
                                                                                        className="w-full flex items-center gap-3 px-4 py-2.5 text-red-500 hover:bg-[#FAF6F0]"
                                                                                >
                                                                                        <LogOut className="w-5 h-5" />
                                                                                        Sign Out
                                                                                </button>
                                                                        </div>
                                                                </div>
                                                        )}
                                                </div>
                                        </>
                                ) : (
                                        <div className="flex gap-4">
                                                <Link
                                                        to="/login"
                                                        className="px-6 py-2.5 bg-[#B76E79] text-white rounded-xl hover:shadow-lg transition-all flex items-center gap-2"
                                                >
                                                        <Lock className="w-5 h-5" />
                                                        Client Login
                                                </Link>
                                        </div>
                                )}
                        </div>

                        {/* Mobile Menu */}
                        <div
                                className={`absolute top-full left-0 w-full bg-white/95 backdrop-blur-xl shadow-2xl border-x border-b border-[#E0D6CC] overflow-hidden transition-all duration-500 ${isMobileMenuOpen
                                                ? "max-h-[400px] py-6 opacity-100 rounded-b-[2.5rem]"
                                                : "max-h-0 opacity-0"
                                        }`}
                        >
                                <div className="space-y-4 px-6">
                                        {['Home', 'Collections', 'Besoke', 'Contact'].map((item) => (
                                                <Link
                                                        key={item}
                                                        to={`/${item.toLowerCase()}`}
                                                        className="flex items-center gap-3 px-4 py-3.5 text-[#6B4E4D] hover:bg-[#FAF6F0] rounded-xl transition-colors"
                                                >
                                                        <div className="w-2 h-2 bg-[#B76E79]/30 rounded-full" />
                                                        {item}
                                                </Link>
                                        ))}
                                        <div className="pt-4 border-t border-[#E0D6CC]">
                                                <Link
                                                        to="/login"
                                                        className="flex items-center gap-3 px-4 py-3.5 text-[#B76E79] hover:bg-[#FAF6F0] rounded-xl"
                                                >
                                                        <Lock className="w-5 h-5" />
                                                        Client Portal
                                                </Link>
                                        </div>
                                </div>
                        </div>
                </nav>
        );
};

export default Navbar;
