import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { User, ShoppingCart, Package, LogOut, Menu, X } from "lucide-react";


const Navbar = () => {
        const [darkMode, setDarkMode] = useState(localStorage.getItem("theme") === "dark");

        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
        const [isScrolled, setIsScrolled] = useState(false);


        useEffect(() => {
                if (darkMode) {
                        document.documentElement.classList.add("dark");
                        localStorage.setItem("theme", "dark");
                } else {
                        document.documentElement.classList.remove("dark");
                        localStorage.setItem("theme", "light");
                }
        }, [darkMode]);

        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);
                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        useEffect(() => {
                const handleScroll = () => {
                        const offset = 100; // Adjust this value to change when the transformation triggers
                        setIsScrolled(window.scrollY > offset);
                };

                // Check initial scroll position
                handleScroll();

                window.addEventListener('scroll', handleScroll);
                return () => window.removeEventListener('scroll', handleScroll);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (
                <nav
    className={`fixed left-1/2 transform -translate-x-1/2 z-50 flex items-center justify-between px-6 py-3 transition-all duration-300 ${
        isScrolled 
            ? 'top-6 w-[90%] md:w-[80%] shadow-xl bg-white/70 backdrop-blur-lg border border-[#e0d6cc]' 
            : 'top-0 w-full bg-gradient-to-b from-[#fff8f5] to-[#fceeea] shadow-sm border-b-2 border-[#f5e6e0]'
    } ${
        isMobileMenuOpen 
            ? "rounded-t-2xl rounded-b-none" 
            : (isScrolled ? "rounded-2xl" : "")
    }`}
>
    {/* Add this decorative element just inside the nav opening tag */}
    {!isScrolled && (
        <div className="absolute inset-x-0 -bottom-px flex justify-center">
            <div className="w-[95%] h-px bg-gradient-to-r from-transparent via-[#e0c8b8] to-transparent" />
        </div>
    )}

    {/* Keep the existing logo container but add hover animation */}
    <div className="flex items-center space-x-3 relative">
        <button
            className="md:hidden text-gray-800 hover:text-[#b76e79] transition"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
            {isMobileMenuOpen ? <X className="w-7 h-7" /> : <Menu className="w-7 h-7" />}
        </button>
        <Link to="/" className="hover:scale-105 transition-transform duration-300">
            <img 
                src="/logo.png" 
                alt="JewelStore" 
                className={`h-12 md:h-14 transition-all duration-300 ${
                    !isScrolled ? 'drop-shadow-[0_4px_4px_rgba(183,110,121,0.15)]' : ''
                }`}
            />
        </Link>
    </div>
                        {/* Rest of your navbar content remains the same */}
                        <div className="hidden md:flex space-x-8 text-gray-900 font-medium text-lg">
                                <Link to="/" className="relative group transition">
                                        Home
                                        <span className="absolute bottom-0 left-1/2 w-0 h-0.5 bg-[#b76e79] group-hover:w-full group-hover:left-0 transition-all duration-300"></span>
                                </Link>
                                <Link to="/products" className="relative group transition">
                                        Products
                                        <span className="absolute bottom-0 left-1/2 w-0 h-0.5 bg-[#b76e79] group-hover:w-full group-hover:left-0 transition-all duration-300"></span>
                                </Link>
                                <Link to="/contact" className="relative group transition">
                                        Contact
                                        <span className="absolute bottom-0 left-1/2 w-0 h-0.5 bg-[#b76e79] group-hover:w-full group-hover:left-0 transition-all duration-300"></span>
                                </Link>
                        </div>
                        {/* User Actions */}
                        <div className="flex items-center space-x-5">
                                {isAuthenticated ? (
                                        <>
                                                <Link to="/cart">
                                                        <ShoppingCart className="w-7 h-7 text-gray-800 hover:text-[#b76e79] transition" />
                                                </Link>
                                                <Link to="/order-history">
                                                        <Package className="w-7 h-7 text-gray-800 hover:text-[#b76e79] transition" />
                                                </Link>
                                                <div className="relative">
                                                        <button
                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                className="relative focus:outline-none"
                                                        >
                                                                <User className="w-7 h-7 text-gray-800 hover:text-[#b76e79] transition" />
                                                        </button>
                                                        {isDropdownOpen && (
                                                                <div className="absolute right-0 mt-3 w-44 bg-white shadow-lg rounded-lg py-2 border border-[#e0d6cc]">
                                                                        <Link to="/profile" className="block px-4 py-2 hover:bg-gray-100">Profile</Link>
                                                                        <Link to="/wishlist" className="block px-4 py-2 hover:bg-gray-100">Wishlist</Link>
                                                                        <button
                                                                                onClick={handleLogout}
                                                                                className="block w-full text-left px-4 py-2 text-red-600 hover:bg-gray-100"
                                                                        >
                                                                                <LogOut className="w-4 h-4 inline-block mr-2" /> Logout
                                                                        </button>
                                                                </div>
                                                        )}
                                                </div>
                                        </>
                                ) : (
                                        <Link to="/login" className="px-6 py-2 bg-[#b76e79] text-white rounded-full shadow-md hover:bg-[#9d5a64] transition">
                                                Login
                                        </Link>
                                )}
                        </div>

                        {/* Mobile Menu */}
                        <div
                                className={`absolute top-full left-0 w-full bg-white/70 backdrop-blur-lg shadow-xl px-6 py-3 border border-[#e0d6cc] overflow-hidden transition-all duration-500 ${isMobileMenuOpen ? "max-h-[300px] py-4 opacity-100 rounded-b-2xl" : "max-h-0 opacity-0"
                                        }`}
                        >
                                <Link to="/" className="block py-3 text-lg text-gray-900 hover:ml-5 hover:font-bold transition-all">
                                        Home
                                </Link>
                                <Link to="/products" className="block py-3 text-lg text-gray-900 hover:ml-5 hover:font-bold transition-all">
                                        Products
                                </Link>
                                <Link to="/contact" className="block py-3 text-lg text-gray-900 hover:ml-5 hover:font-bold transition-all">
                                        Contact
                                </Link>
                        </div>
                </nav>
        );
};

export default Navbar;
