import { useState } from "react";
import axios from "axios";
import { Mail, ArrowLeft } from "lucide-react";

const ForgotPassword = () => {
        const [email, setEmail] = useState("");
        const [message, setMessage] = useState("");
        const [error, setError] = useState("");

        const handleSubmit = async (e) => {
                e.preventDefault();
                setMessage("");
                setError("");

                try {
                        const { data } = await axios.post("http://localhost:5000/api/users/forgot-password", { email });
                        setMessage(data.message);
                } catch (err) {
                        setError(err.response?.data?.message || "Error sending reset email.");
                }
        };

        return (
                <div className="flex items-center justify-center min-h-screen bg-[#FAF6F0]">
                        <div className="w-full max-w-lg bg-[#FCFAF7] backdrop-blur-sm rounded-3xl p-10 border border-[#F5EDE1] shadow-lg mx-4 relative overflow-hidden">
                                {/* Decorative Elements */}
                                <div className="absolute -top-20 -left-20 w-64 h-64 bg-[#D8B6A4]/20 rounded-full blur-xl"></div>
                                <div className="absolute -bottom-20 -right-20 w-64 h-64 bg-[#B76E79]/10 rounded-full blur-xl"></div>

                                <h2 className="text-4xl font-serif text-[#6B4E4D] text-center mb-6">Reset Password</h2>
                                <p className="text-[#6B4E4D]/80 text-center mb-8 max-w-md mx-auto">
                                        Enter your email to receive a golden reset link
                                </p>

                                <form onSubmit={handleSubmit} className="space-y-6">
                                        <div className="relative group">
                                                <div className="absolute inset-y-0 left-4 flex items-center">
                                                        <Mail className="w-6 h-6 text-[#B76E79]" />
                                                </div>
                                                <input
                                                        type="email"
                                                        placeholder="Your registered email"
                                                        value={email}
                                                        onChange={(e) => setEmail(e.target.value)}
                                                        required
                                                        className="w-full px-5 py-4 pl-14 bg-white border border-[#F5EDE1] rounded-xl text-[#6B4E4D] placeholder-[#C5A880] focus:ring-2 focus:ring-[#B76E79]/40 focus:border-[#B76E79]/30 focus:outline-none transition-all"
                                                />
                                        </div>

                                        <button
                                                type="submit"
                                                className="w-full bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white py-4 rounded-xl font-semibold shadow-md hover:shadow-lg transition-all duration-300"
                                        >
                                                Send Rose Gold Link
                                        </button>
                                </form>

                                {/* Status Messages */}
                                {message && <p className="mt-6 text-center text-[#6B4E4D] text-sm">{message}</p>}
                                {error && <p className="mt-6 text-center text-[#B76E79] text-sm">{error}</p>}

                                <div className="mt-8 text-center">
                                        <a
                                                href="/login"
                                                className="text-[#6B4E4D] hover:text-[#B76E79] text-sm font-medium transition-colors flex items-center justify-center gap-2"
                                        >
                                                <ArrowLeft className="w-4 h-4" />
                                                Return to Golden Login
                                        </a>
                                </div>
                        </div>
                </div>
        );
};

export default ForgotPassword;
