import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const CollectionCard = ({ collection, isFeatured }) => {
  const {
    _id,
    name,
    description,
    image,
    category,
    productCount = 0 // We'll get this from the backend
  } = collection;

  const cardVariants = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 },
    hover: { 
      scale: isFeatured ? 1.02 : 1.01,
      transition: { duration: 0.2 }
    }
  };

  return (
    <motion.div
      variants={cardVariants}
      initial="initial"
      animate="animate"
      whileHover="hover"
      className={`bg-white rounded-lg shadow-md overflow-hidden transition-all duration-300 ${
        isFeatured ? 'shadow-lg hover:shadow-xl' : 'hover:shadow-lg'
      }`}
    >
      <Link to={`/collections/${_id}`} className="block h-full">
        <div className={`relative ${isFeatured ? 'aspect-w-21 aspect-h-9' : 'aspect-w-16 aspect-h-9'}`}>
          <img
            src={image || '/placeholder-collection.jpg'}
            alt={name}
            className={`w-full object-cover ${
              isFeatured ? 'h-96' : 'h-full'
            }`}
            onError={(e) => {
              e.target.src = '/placeholder-collection.jpg';
            }}
          />
          {category && (
            <span className={`absolute top-2 right-2 bg-primary-600 text-white px-3 py-1 rounded-full ${
              isFeatured ? 'text-base' : 'text-sm'
            }`}>
              {category.name}
            </span>
          )}
          {isFeatured && (
            <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />
          )}
        </div>
        
        <div className={`p-4 ${isFeatured ? 'p-6' : ''}`}>
          <h3 className={`font-semibold text-gray-800 mb-2 line-clamp-1 ${
            isFeatured ? 'text-2xl' : 'text-lg'
          }`}>
            {name}
          </h3>
          
          <p className={`text-gray-600 line-clamp-2 ${
            isFeatured ? 'text-base mb-4' : 'text-sm mb-3'
          }`}>
            {description}
          </p>
          
          <div className="flex items-center justify-between">
            <span className={`text-gray-500 ${isFeatured ? 'text-base' : 'text-sm'}`}>
              {productCount} {productCount === 1 ? 'Product' : 'Products'}
            </span>
            <span className={`text-primary-600 font-medium ${
              isFeatured ? 'text-base' : 'text-sm'
            }`}>
              View Collection â†’
            </span>
          </div>
        </div>
      </Link>
    </motion.div>
  );
};

export default CollectionCard;
