import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                // If using different ports, specify the full URL for the API
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError("Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);

        if (loading) return <div>Loading...</div>;
        if (error) return <div>{error}</div>;

        return (
                <div className="container mx-auto p-4">
                        {product && (
                                <div className="flex flex-col lg:flex-row">
                                        <div className="flex-1">
                                                <img
                                                        src={product.image} // Make sure your product object contains an image URL
                                                        alt={product.name}
                                                        className="w-full h-auto object-cover"
                                                />
                                        </div>
                                        <div className="flex-1 pl-4">
                                                <h2 className="text-2xl font-semibold">{product.name}</h2>
                                                <p className="text-lg text-gray-700">{product.description}</p>
                                                <div className="mt-4">
                                                        <span className="text-xl font-bold">${product.price}</span>
                                                </div>
                                                <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded">
                                                        Add to Cart
                                                </button>
                                        </div>
                                </div>
                        )}
                </div>
        );
};

export default SingleProduct;
