import { useEffect, useState } from "react";
import { Payments } from "@square/web-payments-sdk";

const SquarePayment = ({ finalAmount, onPaymentSuccess }) => {
        const [paymentInstance, setPaymentInstance] = useState(null);
        const [error, setError] = useState("");

        useEffect(() => {
                async function initializeSquare() {
                        try {
                                const payments = Payments("YOUR_SQUARE_APP_ID", "LKPJNXYZJKCEX");
                                const card = await payments.card();
                                await card.attach("#card-container");
                                setPaymentInstance(card);
                        } catch (error) {
                                setError("Failed to load payment form.");
                        }
                }
                initializeSquare();
        }, []);

        const handlePayment = async () => {
                if (!paymentInstance) return;

                const result = await paymentInstance.tokenize();
                if (result.status === "OK") {
                        onPaymentSuccess(result.token);
                } else {
                        alert("Card Tokenization Failed!");
                }
        };

        return (
                <div className="p-6 bg-white rounded-lg shadow-md">
                        <h3 className="text-lg font-bold mb-4">Enter Card Details</h3>
                        <div id="card-container"></div>
                        <button
                                onClick={handlePayment}
                                className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700"
                        >
                                Pay â‚¹{finalAmount.toFixed(2)}
                        </button>
                        {error && <p className="text-red-500 mt-2">{error}</p>}
                </div>
        );
};

export default SquarePayment;
