import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Heart, Star, ShoppingCart, Trash2, HeartOff, CheckCircle } from 'lucide-react';

const Wishlist = () => {
        const [wishlist, setWishlist] = useState([]);
        const [loading, setLoading] = useState(true);
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchWishlist = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        alert("Please log in to view your wishlist.");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/wishlist", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setWishlist(data.wishlist?.products || []);
                        } catch (error) {
                                setMessage(error.response?.data?.message || "Failed to fetch wishlist.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchWishlist();
        }, []);

        const removeFromWishlist = async (productId) => {
                try {
                        const token = localStorage.getItem("userToken");
                        await axios.delete(`http://localhost:5000/api/wishlist/remove/${productId}`, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setWishlist(wishlist.filter((item) => item._id !== productId));
                        setMessage("❌ Removed from wishlist.");
                } catch (error) {
                        setMessage(error.response?.data?.message || "Failed to remove item from wishlist.");
                }
                hideMessage(); // Auto-hide message after 3 seconds
        };

        const addToCart = async (productId) => {
                try {
                        const token = localStorage.getItem("userToken");
                        const userId = localStorage.getItem("userId");

                        if (!userId || !productId) {
                                setMessage("⚠️ Missing userId or productId.");
                                console.error("🚨 Missing values:", { userId, productId });
                                hideMessage();
                                return;
                        }

                        console.log("📤 Sending request to add product to cart:", { userId, productId });

                        await axios.post(
                                "http://localhost:5000/api/cart/add",
                                { userId, productId },
                                { headers: { Authorization: `Bearer ${token}` } }
                        );

                        setMessage("✅ Product added to cart successfully!");
                } catch (error) {
                        setMessage(error.response?.data?.message || "❌ Failed to add product to cart.");
                        console.error("❌ Error adding to cart:", error);
                }
                hideMessage(); // Auto-hide message after 3 seconds
        };

        // Function to auto-hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };



        if (loading) return <div className="text-center text-gray-600 py-10">Loading wishlist...</div>;

        return (
                <div className="mx-auto mt-[60px] px-[5%] py-16 bg-[#FAF6F0] min-h-screen relative overflow-hidden">
                        {/* Decorative Elements (Consistent with other pages) */}
                        <div className="absolute top-20 left-20 w-48 h-48 bg-[#B76E79]/10 rounded-full blur-2xl"></div>
                        <div className="absolute bottom-32 right-32 w-64 h-64 bg-[#C5A880]/10 rounded-full blur-2xl"></div>

                        <div className="max-w-7xl mx-auto">
                                {/* Header (Consistent typography) */}
                                <div className="flex items-center gap-4 mb-12">
                                        <Heart className="w-8 h-8 text-[#B76E79]" />
                                        <h1 className="text-4xl font-serif text-[#6B4E4D]">Curated Favorites</h1>
                                </div>

                                {/* Unified Card Grid */}
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                                        {wishlist.map((item) => (
                                                <div key={item._id} className="group relative bg-white/90 backdrop-blur-sm rounded-2xl shadow-sm hover:shadow-xl transition-all duration-300 border border-[#E0D6CC]">
                                                        {/* Image Container (Matches product cards) */}
                                                        <div className="relative aspect-square overflow-hidden rounded-t-2xl">
                                                                <div className="absolute inset-0 bg-gradient-to-t from-[#6B4E4D]/20 to-transparent z-10" />
                                                                <img
                                                                        src={item.image}
                                                                        alt={item.name}
                                                                        className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-500"
                                                                />
                                                                {/* Floating Badge (Like product cards) */}
                                                                <span className="absolute top-4 left-4 bg-[#B76E79]/90 text-white px-4 py-1.5 rounded-full text-sm font-medium shadow-md backdrop-blur-sm z-20">
                                                                        Wishlisted
                                                                </span>
                                                        </div>

                                                        {/* Card Content (Consistent padding/spacing) */}
                                                        <div className="p-6 space-y-4">
                                                                {/* Title (Same as product pages) */}
                                                                <h4 className="text-xl font-serif text-[#6B4E4D]">{item.name}</h4>

                                                                {/* Price & Rating (Matching detail layout) */}
                                                                <div className="flex items-center justify-between">
                                                                        <p className="text-lg font-medium text-[#B76E79]">${item.price?.toFixed(2)}</p>
                                                                        <div className="flex items-center gap-1 text-[#C5A880]">
                                                                                <Star className="w-4 h-4 fill-current" />
                                                                                <span>{item.rating?.toFixed(1)}</span>
                                                                        </div>
                                                                </div>

                                                                {/* Action Buttons (Consistent with product cards) */}
                                                                <div className="flex flex-col gap-3">
                                                                        <button
                                                                                onClick={() => navigate(`/product/${item._id}`)}
                                                                                className="w-full py-2.5 text-center bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white rounded-xl hover:shadow-lg transition-all"
                                                                        >
                                                                                View Details
                                                                        </button>
                                                                        <div className="grid grid-cols-2 gap-3">
                                                                                <button
                                                                                        onClick={() => addToCart(item._id)}
                                                                                        className="flex items-center justify-center gap-2 bg-[#B76E79]/10 text-[#B76E79] hover:bg-[#B76E79] hover:text-white py-2.5 rounded-xl transition-all"
                                                                                >
                                                                                        <ShoppingCart className="w-4 h-4" />
                                                                                        Add to Cart
                                                                                </button>
                                                                                <button
                                                                                        onClick={() => removeFromWishlist(item._id)}
                                                                                        className="flex items-center justify-center gap-2 bg-[#B76E79]/10 text-[#B76E79] hover:bg-[#B76E79] hover:text-white py-2.5 rounded-xl transition-all"
                                                                                >
                                                                                        <Trash2 className="w-4 h-4" />
                                                                                        Remove
                                                                                </button>
                                                                        </div>
                                                                </div>
                                                        </div>
                                                </div>
                                        ))}
                                </div>

                                {/* Empty State (Matches other empty states) */}
                                {!wishlist.length && (
                                        <div className="text-center py-20 space-y-6">
                                                <div className="inline-block p-6 bg-[#B76E79]/10 rounded-full">
                                                        <HeartOff className="w-12 h-12 text-[#B76E79]" />
                                                </div>
                                                <p className="text-2xl font-serif text-[#6B4E4D]">Your Curated Collection Awaits</p>
                                                <button
                                                        onClick={() => navigate('/products')}
                                                        className="px-6 py-3 bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white rounded-xl hover:shadow-lg transition-all"
                                                >
                                                        Explore Collection
                                                </button>
                                        </div>
                                )}

                                {/* Status Message (Consistent with other pages) */}
                                {message && (
                                        <div className="mt-6 p-3 bg-emerald-100 text-emerald-700 rounded-xl flex items-center gap-2">
                                                <CheckCircle className="w-5 h-5" />
                                                {message}
                                        </div>
                                )}
                        </div>
                </div>

        );
};

export default Wishlist;
