import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Profile = () => {
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        alert("Please log in to view your profile.");
                                        navigate("/login");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 flex justify-center py-10 px-4">
                        <div className="w-full max-w-4xl bg-white rounded-2xl shadow-lg p-8 border border-gray-200 relative overflow-hidden">
                                <div className="absolute top-0 left-0 w-full h-32 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-t-2xl"></div>
                                <div className="relative flex items-center space-x-6 border-b pb-6 pt-10">
                                        <img
                                                src={user?.avatar || "https://via.placeholder.com/150"}
                                                alt="User Avatar"
                                                className="w-24 h-24 rounded-full border-4 border-white shadow-md"
                                        />
                                        <div>
                                                <h1 className="text-2xl font-bold text-gray-800">{user?.name}</h1>
                                                <p className="text-gray-500 text-sm">{user?.email}</p>
                                        </div>
                                        <button onClick={() => navigate("/edit-profile")} className="ml-auto flex items-center bg-gray-900 text-white px-4 py-2 rounded-lg shadow-md hover:bg-gray-700 transition-all duration-300">
                                                Edit Profile
                                        </button>
                                </div>
                                <div className="mt-6 grid grid-cols-2 gap-6 border-b pb-6 text-gray-700">
                                        <div className="flex items-center space-x-3">
                                                <p><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                        </div>
                                        <div className="flex items-center space-x-3">
                                                <p><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                        </div>
                                </div>
                                <div className="mt-6 flex justify-center">
                                        <button onClick={handleLogout} className="px-4 py-2 bg-red-600 text-white rounded-md">Logout</button>
                                </div>
                        </div>
                </div>
        );
};

export default Profile;
