import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { MapPin, Phone, Edit, LogOut } from "lucide-react";


const Profile = () => {
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;
        if (error) return <div className="text-center text-red-500">{error}</div>;

        return (
                <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-blue-100 to-purple-200 px-6">
                        <div className="w-full max-w-3xl bg-white/80 shadow-2xl rounded-2xl backdrop-blur-lg border border-gray-200 p-8 flex gap-6">
                                {/* Left Section - Profile Info */}
                                <div className="flex flex-col items-center w-1/2 p-6 bg-white/50 rounded-xl shadow-md">
                                        <img
                                                className="w-28 h-28 rounded-full border-4 border-blue-500 shadow-lg"
                                                src={`https://ui-avatars.com/api/?name=${user?.name}&background=0D8ABC&color=fff`}
                                                alt="User Avatar"
                                        />
                                        <h1 className="text-2xl font-extrabold text-gray-900 mt-4">{user?.name}</h1>
                                        <p className="text-gray-700 text-lg">{user?.email}</p>

                                        <div className="mt-4 p-4 border border-gray-300 rounded-lg bg-white/70 shadow-sm space-y-3 w-full">
                                                <div className="flex items-center gap-3 text-gray-800">
                                                        <Phone className="text-blue-600 w-6 h-6" />
                                                        <p><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                                </div>
                                                <div className="flex items-center gap-3 text-gray-800">
                                                        <MapPin className="text-blue-600 w-6 h-6" />
                                                        <p><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                                </div>
                                        </div>
                                </div>

                                {/* Right Section - Actions */}
                                <div className="flex flex-col justify-center w-1/2 space-y-4">
                                        <button
                                                onClick={() => navigate("/edit-profile")}
                                                className="flex items-center justify-center gap-3 w-full bg-gradient-to-r from-blue-500 to-purple-500 text-white py-3 px-6 rounded-lg hover:scale-105 transition-all shadow-md"
                                        >
                                                <Edit className="w-6 h-6" /> Edit Profile
                                        </button>
                                        <button
                                                onClick={handleLogout}
                                                className="flex items-center justify-center gap-3 w-full bg-gradient-to-r from-red-500 to-orange-500 text-white py-3 px-6 rounded-lg hover:scale-105 transition-all shadow-md"
                                        >
                                                <LogOut className="w-6 h-6" /> Logout
                                        </button>
                                </div>
                        </div>
                </div>

        );
};

export default Profile;
