import React, { useState, useEffect } from "react";

const Cart = () => {
        const [cartItems, setCartItems] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const userId = localStorage.getItem("userId"); // Get user ID
        const token = localStorage.getItem("userToken"); // Get token

        useEffect(() => {
                if (!userId || !token) {
                        setError("Please log in to view your cart.");
                        setLoading(false);
                        return;
                }

                const fetchCart = async () => {
                        console.log("ðŸ“¡ Fetching cart for user:", userId);
                        console.log("ðŸ”‘ Sending token:", token);

                        try {
                                const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                                        method: "GET",
                                        headers: {
                                                "Content-Type": "application/json",
                                                Authorization: `Bearer ${token}`,
                                        },
                                });

                                console.log("ðŸŸ¢ Response status:", response.status);
                                const data = await response.json();
                                console.log("ðŸ“¦ Response data:", data);

                                if (response.ok) {
                                        setCartItems(data.products || []);
                                } else {
                                        setError(data.message || "Failed to load cart.");
                                }
                        } catch (err) {
                                console.error("âŒ Fetch error:", err);
                                setError("Server error while fetching cart.");
                        } finally {
                                setLoading(false);
                        }
                };

                fetchCart();
        }, [userId, token]);

        const updateQuantity = (id, amount) => {
                setCartItems((prevItems) =>
                        prevItems.map((item) =>
                                item.product._id === id ? { ...item, quantity: Math.max(1, item.quantity + amount) } : item
                        )
                );
        };

        const removeItem = (id) => {
                setCartItems((prevItems) => prevItems.filter((item) => item.product._id !== id));
        };

        const subtotal = cartItems.reduce((acc, item) => acc + item.product.price * item.quantity, 0);
        const tax = subtotal * 0.1; // Example 10% tax
        const shipping = subtotal > 0 ? 10 : 0;
        const total = subtotal + tax + shipping;

        if (loading) return <div>Loading...</div>;
        if (error) return <div className="text-red-600">{error}</div>;

        return (
                <div className="max-w-6xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10 grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                                <h2 className="text-2xl font-bold mb-6">Shopping Cart</h2>
                                {cartItems.length === 0 ? (
                                        <p className="text-gray-500">Your cart is empty.</p>
                                ) : (
                                        <div>
                                                {cartItems.map((item) => (
                                                        <div key={item.product._id} className="flex justify-between items-center p-4 border-b">
                                                                <div className="flex items-center gap-4">
                                                                        <img src={item.product.image} alt={item.product.name} className="w-16 h-16 object-cover rounded" />
                                                                        <div>
                                                                                <h3 className="text-lg font-semibold">{item.product.name}</h3>
                                                                                <p className="text-gray-600">${item.product.price.toFixed(2)}</p>
                                                                        </div>
                                                                </div>
                                                                <div className="flex items-center gap-2">
                                                                        <button
                                                                                onClick={() => updateQuantity(item.product._id, -1)}
                                                                                className="px-3 py-1 bg-gray-300 text-gray-700 rounded"
                                                                        >
                                                                                -
                                                                        </button>
                                                                        <span className="w-8 text-center">{item.quantity}</span>
                                                                        <button
                                                                                onClick={() => updateQuantity(item.product._id, 1)}
                                                                                className="px-3 py-1 bg-gray-300 text-gray-700 rounded"
                                                                        >
                                                                                +
                                                                        </button>
                                                                </div>
                                                                <p className="text-gray-800 font-semibold">${(item.product.price * item.quantity).toFixed(2)}</p>
                                                                <button
                                                                        onClick={() => removeItem(item.product._id)}
                                                                        className="px-3 py-1 bg-red-500 text-white rounded"
                                                                >
                                                                        Remove
                                                                </button>
                                                        </div>
                                                ))}
                                        </div>
                                )}
                        </div>
                        <div className="p-4 bg-gray-100 rounded-lg">
                                <h3 className="text-xl font-semibold mb-4">Cart Summary</h3>
                                <div className="flex justify-between mb-2">
                                        <span>Subtotal:</span>
                                        <span>${subtotal.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between mb-2">
                                        <span>Tax (10%):</span>
                                        <span>${tax.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between mb-2">
                                        <span>Shipping:</span>
                                        <span>${shipping.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between font-bold text-lg mt-2">
                                        <span>Total:</span>
                                        <span>${total.toFixed(2)}</span>
                                </div>
                                <button className="w-full mt-4 bg-blue-600 text-white py-2 rounded-lg font-semibold hover:bg-blue-700">
                                        Proceed to Checkout
                                </button>
                        </div>
                </div>
        );
};

export default Cart;
