import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { User, Mail, Phone, MapPin, Save } from "lucide-react";

const EditProfile = () => {
        const [formData, setFormData] = useState({ name: "", email: "", phone: "", address: "" });
        const [loading, setLoading] = useState(true);
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setFormData({ name: data.name, email: data.email, phone: data.phone, address: data.address });
                        } catch (error) {
                                setMessage("❌ Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleChange = (e) => {
                setFormData({ ...formData, [e.target.name]: e.target.value });
        };

        const handleSubmit = async (e) => {
                e.preventDefault();
                try {
                        const token = localStorage.getItem("userToken");

                        await axios.put("http://localhost:5000/api/users/profile", formData, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setMessage("✅ Profile updated successfully!");

                        setTimeout(() => {
                                setMessage(""); // Clear message before navigating
                                navigate("/profile");
                        }, 1500);
                } catch (error) {
                        setMessage("❌ Failed to update profile.");
                }

                hideMessage();
        };

        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };

        if (loading)
                return (
                        <div className="flex items-center justify-center min-h-screen text-xl font-semibold text-blue-600">
                                Loading...
                        </div>
                );

        return (
                <div className="flex justify-center items-center min-h-screen bg-gray-100 px-4">
                        <div className="w-full max-w-lg bg-white shadow-xl rounded-xl p-8">
                                {/* Header */}
                                <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Edit Profile</h2>
                                {message && <p className="text-center text-red-500 mb-4">{message}</p>}

                                {/* Form */}
                                <form onSubmit={handleSubmit} className="space-y-5">
                                        {/* Input Fields with Icons */}
                                        {[
                                                { name: "name", placeholder: "Full Name", icon: <User className="w-5 h-5 text-gray-500" /> },
                                                { name: "email", placeholder: "Email", icon: <Mail className="w-5 h-5 text-gray-500" /> },
                                                { name: "phone", placeholder: "Phone Number", icon: <Phone className="w-5 h-5 text-gray-500" /> },
                                                { name: "address", placeholder: "Address", icon: <MapPin className="w-5 h-5 text-gray-500" /> }
                                        ].map(({ name, placeholder, icon }) => (
                                                <div key={name} className="relative">
                                                        <div className="absolute inset-y-0 left-3 flex items-center">{icon}</div>
                                                        <input
                                                                className="w-full p-4 pl-12 border border-[#D8B6A4] bg-[#FAF5F0] rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-[#B88E8D]"
                                                                name={name}
                                                                placeholder={placeholder}
                                                                value={formData[name]}
                                                                onChange={handleChange}
                                                        />
                                                </div>
                                        ))}

                                        {/* Save Button */}
                                        <button type="submit" className="w-full flex items-center justify-center gap-2 bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 transition">
                                                <Save className="w-5 h-5" /> Save Changes
                                        </button>
                                </form>
                        </div>
                </div>
        );
};

export default EditProfile;
