import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { User, Mail, Phone, MapPin, Save, Camera } from "lucide-react";

const EditProfile = () => {
        const [formData, setFormData] = useState({ name: "", email: "", phone: "", address: "" });
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setFormData({ name: data.name, email: data.email, phone: data.phone, address: data.address });
                        } catch (error) {
                                setMessage("❌ Failed to fetch user profile.");
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleChange = (e) => {
                setFormData({ ...formData, [e.target.name]: e.target.value });
        };

        const handleSubmit = async (e) => {
                e.preventDefault();
                try {
                        const token = localStorage.getItem("userToken");

                        await axios.put("http://localhost:5000/api/users/profile", formData, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setMessage("✅ Profile updated successfully!");

                        setTimeout(() => {
                                setMessage("");
                                navigate("/profile");
                        }, 1500);
                } catch (error) {
                        setMessage("❌ Failed to update profile.");
                }

                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };

        return (
                <div className="relative z-10 w-full max-w-xl bg-white/90 backdrop-blur-sm rounded-2xl border border-[#E0D6CC] shadow-2xl p-10 mt-20 mx-auto">
                        {/* Floating Decorative Elements */}
                        <div className="absolute -top-20 left-1/4 w-32 h-32 bg-gradient-to-r from-[#B76E79]/15 to-[#C5A880]/15 rounded-full blur-2xl"></div>
                        <div className="absolute -bottom-20 right-1/4 w-40 h-40 bg-gradient-to-b from-[#C5A880]/10 to-transparent rounded-full blur-xl"></div>

                        {/* Profile Avatar Section */}
                        <div className="flex flex-col items-center mb-10">
                                <div className="relative group">
                                        <div className="absolute inset-0 bg-[#B76E79]/20 rounded-full blur-lg animate-pulse"></div>
                                        <img
                                                src={`https://ui-avatars.com/api/?name=${encodeURIComponent(formData.name || "User")}&background=FAF6F0&color=B76E79&size=150`}
                                                alt="Profile"
                                                className="w-32 h-32 rounded-full border-4 border-[#B76E79]/50 shadow-xl relative z-10"
                                        />
                                        <button className="absolute bottom-2 right-2 z-20 bg-gradient-to-br from-[#B76E79] to-[#C5A880] p-2 rounded-full text-white shadow-lg hover:scale-105 transition-transform">
                                                <Camera className="w-6 h-6" />
                                        </button>
                                </div>
                                <h2 className="text-3xl font-serif text-[#6B4E4D] mt-6">Edit Profile</h2>
                                {message && <p className="text-center text-[#B76E79] mt-3 text-sm">{message}</p>}
                        </div>

                        {/* Form Section */}
                        <form onSubmit={handleSubmit} className="space-y-6">
                                {[
                                        { name: "name", placeholder: "Full Name", icon: <User className="w-6 h-6 text-[#B76E79]" /> },
                                        { name: "email", placeholder: "Email Address", icon: <Mail className="w-6 h-6 text-[#B76E79]" /> },
                                        { name: "phone", placeholder: "Contact Number", icon: <Phone className="w-6 h-6 text-[#B76E79]" /> },
                                        { name: "address", placeholder: "Shipping Address", icon: <MapPin className="w-6 h-6 text-[#B76E79]" /> }
                                ].map(({ name, placeholder, icon }) => (
                                        <div key={name} className="relative group">
                                                <div className="absolute inset-y-0 left-4 flex items-center">{icon}</div>
                                                <input
                                                        className="w-full px-5 py-4 pl-14 bg-[#FAF6F0] border border-[#E0D6CC] rounded-xl text-[#6B4E4D] placeholder-[#B76E79]/50 focus:ring-2 focus:ring-[#B76E79]/50 focus:outline-none transition-all"
                                                        name={name}
                                                        placeholder={placeholder}
                                                        value={formData[name]}
                                                        onChange={handleChange}
                                                />
                                        </div>
                                ))}

                                {/* Save Button */}
                                <button
                                        type="submit"
                                        className="w-full bg-gradient-to-r from-[#B76E79] to-[#C5A880] hover:from-[#C5A880] hover:to-[#B76E79] text-white py-4 rounded-xl font-medium shadow-lg hover:shadow-xl transition-all duration-300 flex items-center justify-center gap-3"
                                >
                                        <Save className="w-6 h-6" />
                                        Update Profile
                                </button>
                        </form>
                </div>
        );
};

export default EditProfile;
