import { useState } from 'react';

export default function AddUserModal({ isOpen, onClose, onAddUser }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      id: `usr_${Math.random().toString(36).substr(2, 9)}`,
      name: formData.name,
      email: formData.email,
      cartItems: 0,
      wishlistItems: 0,
      orders: 0
    };
    onAddUser(newUser);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-6 w-full max-w-md">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-serif text-rose-800">Add New User</h2>
          <button onClick={onClose} className="text-amber-900 hover:text-rose-800">
            âœ•
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-amber-900 text-sm font-medium mb-1">Full Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              className="w-full px-3 py-2 border border-rose-200 rounded-lg focus:ring-2 focus:ring-rose-300 focus:border-transparent"
              required
            />
          </div>
          
          <div className="mb-4">
            <label className="block text-amber-900 text-sm font-medium mb-1">Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              className="w-full px-3 py-2 border border-rose-200 rounded-lg focus:ring-2 focus:ring-rose-300 focus:border-transparent"
              required
            />
          </div>
          
          <div className="mb-6">
            <label className="block text-amber-900 text-sm font-medium mb-1">Password</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              className="w-full px-3 py-2 border border-rose-200 rounded-lg focus:ring-2 focus:ring-rose-300 focus:border-transparent"
              required
            />
          </div>
          
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 border border-rose-200 text-amber-900 rounded-lg hover:bg-rose-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-rose-700 text-white rounded-lg hover:bg-rose-800"
            >
              Add User
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
