import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const [message, setMessage] = useState(""); // State for feedback messages


        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError(data.message || "Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        } finally {
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);


        // Function to add product to cart
        const handleAddToCart = async () => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        hideMessage();
                        return;
                }

                console.log("Sending request to add product:", { userId, productId: id });

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();
                        console.log("Response from server:", data);

                        if (response.ok) {
                                setMessage("✅ Added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                hideMessage();
        };

        const handleAddToWishlist = async () => {
                const token = localStorage.getItem("userToken");

                if (!token) {
                        setMessage("⚠️ Please log in to add items to your wishlist.");
                        hideMessage();
                        return;
                }

                try {
                        const response = await axios.post(
                                `http://localhost:5000/api/wishlist/add/${id}`,
                                {},
                                {
                                        headers: { Authorization: `Bearer ${token}` },
                                }
                        );

                        console.log("Wishlist response:", response.data);
                        setMessage("❤️ Added to wishlist!");
                } catch (error) {
                        setMessage(`❌ ${error.response?.data?.message || "Failed to add to wishlist."}`);
                }

                hideMessage();
        };

        // Function to hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };



        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="container mx-auto px-6 py-16">
            {product && (
                <div className="bg-gray-50 shadow-lg rounded-lg overflow-hidden p-8">
                    {/* Product Info Section */}
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
                        {/* Image Section */}
                        <div className="relative group">
                            <img
                                src={product.image}
                                alt={product.name}
                                className="w-full h-auto object-cover rounded-lg shadow-md transition-transform duration-300 group-hover:scale-105"
                            />
                        </div>

                        {/* Product Details */}
                        <div className="space-y-6">
                            <h2 className="text-4xl font-bold text-gray-900">{product.name}</h2>
                            <p className="text-lg text-gray-600 leading-relaxed">{product.description}</p>
                            <span className="text-3xl font-bold text-indigo-600">${product.price}</span>

                            <div className="flex flex-wrap gap-4 mt-6">
                                <button 
                                    onClick={handleAddToCart} 
                                    className="bg-indigo-600 text-white py-3 px-6 rounded-lg hover:bg-indigo-700 transition duration-300 shadow-md"
                                >
                                    Add to Cart
                                </button>
                                <button 
                                    onClick={handleAddToWishlist} 
                                    className="bg-gray-900 text-white py-3 px-6 rounded-lg hover:bg-gray-700 transition duration-300 shadow-md"
                                >
                                    Add to Wishlist
                                </button>
                            </div>
                            {message && <p className="text-green-600 mt-4">{message}</p>}
                        </div>
                    </div>

                    {/* Category Description */}
                    <div className="mt-12 bg-white p-8 rounded-lg shadow-md">
                        <h3 className="text-2xl font-semibold text-gray-900">Category Description</h3>
                        <p className="mt-4 text-gray-600">Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                    </div>

                    {/* Related Products */}
                    <div className="mt-12">
                        <h3 className="text-3xl font-semibold text-gray-900 mb-6">You May Also Like</h3>
                        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                            {[1, 2, 3].map((_, index) => (
                                <div key={index} className="bg-white rounded-lg shadow-lg overflow-hidden transition-transform transform hover:scale-105">
                                    <img
                                        src="https://via.placeholder.com/300"
                                        alt="Related Product"
                                        className="w-full h-56 object-cover"
                                    />
                                    <div className="p-5">
                                        <h4 className="text-xl font-medium text-gray-900">Product Name</h4>
                                        <p className="text-gray-600 mt-2">$39.99</p>
                                        <button className="mt-4 bg-indigo-600 text-white py-2 px-4 rounded-lg hover:bg-indigo-700 transition duration-300 w-full">
                                            Add to Cart
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
        );

};

export default SingleProduct;


