import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { User, ShoppingCart, Package, LogOut, Menu, X } from "lucide-react";


const Navbar = () => {

        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);



        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);
                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (

                <nav
                        className={`fixed top-6 left-1/2 transform -translate-x-1/2 w-[90%] md:w-[80%] bg-white/90 backdrop-blur-xl shadow-lg px-6 py-3 flex items-center justify-between z-50 border border-[#E0D6CC] transition-all duration-300 ${isMobileMenuOpen ? "rounded-t-[2rem] rounded-b-none" : "rounded-[2rem]"
                                }`}
                >
                        {/* Logo & Mobile Menu Toggle */}
                        <div className="flex items-center gap-4">
                                <button
                                        className="md:hidden text-[#6B4E4D] hover:text-[#B76E79] transition-transform"
                                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                >
                                        {isMobileMenuOpen ? (
                                                <X className="w-8 h-8 p-1.5 bg-[#B76E79]/10 rounded-full" />
                                        ) : (
                                                <Menu className="w-8 h-8 p-1.5 bg-[#B76E79]/10 rounded-full" />
                                        )}
                                </button>
                                <Link to="/" className="group">
                                        <img
                                                src="/logo.png"
                                                alt="JewelStore"
                                                className="h-12 transition-transform duration-300 group-hover:scale-105"
                                        />
                                </Link>
                        </div>

                        {/* Desktop Navigation */}
                        <div className="hidden md:flex gap-8">
                                {['Home', 'Collections', 'Contact'].map((item) => (
                                        <Link
                                                key={item}
                                                to={`/${item.toLowerCase()}`}
                                                className="relative px-4 py-2 text-[#6B4E4D] group overflow-hidden"
                                        >
                                                <span className="relative z-10 group-hover:text-[#B76E79] transition-colors">
                                                        {item}
                                                </span>
                                                <div className="absolute inset-0 bg-gradient-to-b from-[#B76E79]/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />
                                        </Link>
                                ))}
                        </div>

                        {/* User Actions */}
                        <div className="flex items-center gap-6">
                                {isAuthenticated ? (
                                        <>
                                                <div className="relative">
                                                        <button
                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                className="p-2 bg-[#B76E79]/10 rounded-lg hover:bg-[#B76E79]/20 transition-colors"
                                                        >
                                                                <User className="w-6 h-6 text-[#B76E79]" />
                                                        </button>

                                                        {isDropdownOpen && (
                                                                <div className="absolute right-0 mt-3 w-48 bg-white/90 backdrop-blur-lg shadow-xl rounded-xl py-3 border border-[#E0D6CC]">
                                                                        <Link
                                                                                to="/profile"
                                                                                className="flex items-center gap-3 px-4 py-2.5 hover:bg-[#FAF6F0]"
                                                                        >
                                                                                <User className="w-5 h-5 text-[#B76E79]" />
                                                                                Profile
                                                                        </Link>
                                                                        <Link
                                                                                to="/wishlist"
                                                                                className="flex items-center gap-3 px-4 py-2.5 hover:bg-[#FAF6F0]"
                                                                        >
                                                                                <Heart className="w-5 h-5 text-[#B76E79]" />
                                                                                Wishlist
                                                                        </Link>
                                                                        <button
                                                                                onClick={handleLogout}
                                                                                className="w-full flex items-center gap-3 px-4 py-2.5 text-red-500 hover:bg-[#FAF6F0]"
                                                                        >
                                                                                <LogOut className="w-5 h-5" />
                                                                                Logout
                                                                        </button>
                                                                </div>
                                                        )}
                                                </div>

                                                <div className="flex gap-5">
                                                        <Link
                                                                to="/cart"
                                                                className="p-2 bg-[#B76E79]/10 rounded-lg hover:bg-[#B76E79]/20 relative"
                                                        >
                                                                <ShoppingCart className="w-6 h-6 text-[#B76E79]" />
                                                                <span className="absolute -top-1 -right-1 bg-[#B76E79] text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                                                                        3
                                                                </span>
                                                        </Link>

                                                        <Link
                                                                to="/orders"
                                                                className="p-2 bg-[#B76E79]/10 rounded-lg hover:bg-[#B76E79]/20"
                                                        >
                                                                <Package className="w-6 h-6 text-[#B76E79]" />
                                                        </Link>
                                                </div>
                                        </>
                                ) : (
                                        <Link
                                                to="/login"
                                                className="px-6 py-2.5 bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white rounded-xl hover:shadow-lg transition-all"
                                        >
                                                Client Login
                                        </Link>
                                )}
                        </div>

                        {/* Mobile Menu */}
                        <div
                                className={`absolute top-full left-0 w-full bg-white/90 backdrop-blur-lg shadow-xl px-6 border-x border-b border-[#E0D6CC] overflow-hidden transition-all duration-500 ${isMobileMenuOpen
                                                ? "max-h-[300px] py-4 opacity-100 rounded-b-[2rem]"
                                                : "max-h-0 opacity-0"
                                        }`}
                        >
                                <div className="space-y-4">
                                        {['Home', 'Collections', 'Contact'].map((item) => (
                                                <Link
                                                        key={item}
                                                        to={`/${item.toLowerCase()}`}
                                                        className="block px-4 py-3 text-[#6B4E4D] hover:bg-[#FAF6F0] rounded-xl transition-colors"
                                                >
                                                        {item}
                                                </Link>
                                        ))}
                                </div>
                        </div>
                </nav>
        );
};

export default Navbar;
