import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { User, ShoppingCart, X, Package, LogOut, Home, Phone, Heart, Menu, Search, Grid } from "lucide-react";


const Navbar = () => {

        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);
        const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);



        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);
                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (
                <nav className="fixed top-6 left-1/2 -translate-x-1/2 w-[92%] max-w-5xl bg-gradient-to-br from-[#FAF6F0]/95 to-[#B76E79]/10 backdrop-blur-2xl shadow-[0_10px_50px_-15px_rgba(183,110,121,0.3)] rounded-2xl border border-[#E0D6CC] z-50 transform-gpu transition-all duration-500 hover:shadow-[0_20px_60px_-10px_rgba(183,110,121,0.4)] hover:-translate-y-1">
                        <div className="relative flex items-center justify-between px-8 py-3">

                                {/* Floating Particles Background */}
                                <div className="absolute inset-0 overflow-hidden rounded-2xl">
                                        {[...Array(12)].map((_, i) => (
                                                <div
                                                        key={i}
                                                        className="absolute w-1 h-1 bg-[#B76E79]/30 rounded-full animate-float"
                                                        style={{
                                                                left: `${Math.random() * 100}%`,
                                                                top: `${Math.random() * 100}%`,
                                                                animationDelay: `${i * 0.5}s`
                                                        }}
                                                />
                                        ))}
                                </div>

                                {/* Logo with Gem Animation */}
                                <Link to="/" className="group relative z-10">
                                        <div className="relative p-2 bg-gradient-to-br from-[#B76E79] to-[#C5A880] rounded-lg transform transition-all duration-500 group-hover:rotate-y-180">
                                                <img
                                                        src="/logo.png"
                                                        className="h-12 w-12 object-contain filter drop-shadow-lg"
                                                        alt="JewelStore"
                                                />
                                                <div className="absolute inset-0 bg-[url('/texture.png')] opacity-20 mix-blend-overlay" />
                                        </div>
                                </Link>

                                {/* Central Navigation with Hover Effects */}
                                <div className="hidden lg:flex items-center space-x-8 z-10">
                                        <Link
                                                to="/"
                                                className="nav-link relative px-4 py-2 text-[#6B4E4D] group overflow-hidden"
                                        >
                                                <span className="relative z-10">Home</span>
                                                <div className="absolute inset-0 bg-gradient-to-br from-[#B76E79] to-[#C5A880] opacity-0 group-hover:opacity-10 transform -skew-x-12 -translate-x-full group-hover:translate-x-0 transition-all duration-500" />
                                        </Link>

                                        <Link
                                                to="/products"
                                                className="nav-link relative px-4 py-2 text-[#6B4E4D] group overflow-hidden"
                                        >
                                                <span className="relative z-10">Collections</span>
                                                <div className="absolute inset-0 bg-gradient-to-br from-[#B76E79] to-[#C5A880] opacity-0 group-hover:opacity-10 transform -skew-x-12 -translate-x-full group-hover:translate-x-0 transition-all duration-500" />
                                        </Link>

                                        <Link
                                                to="/contact"
                                                className="nav-link relative px-4 py-2 text-[#6B4E4D] group overflow-hidden"
                                        >
                                                <span className="relative z-10">Concierge</span>
                                                <div className="absolute inset-0 bg-gradient-to-br from-[#B76E79] to-[#C5A880] opacity-0 group-hover:opacity-10 transform -skew-x-12 -translate-x-full group-hover:translate-x-0 transition-all duration-500" />
                                        </Link>
                                </div>

                                {/* Right Section - Interactive Elements */}
                                <div className="flex items-center gap-6 z-10">

                                        {/* Cart with Floating Gem Counter */}
                                        <Link to="/cart" className="relative p-2 group">
                                                <div className="relative">
                                                        <ShoppingCart className="w-7 h-7 text-[#6B4E4D] group-hover:text-[#B76E79] transition-colors" />
                                                        <div className="absolute -top-2 -right-2 w-6 h-6 bg-gradient-to-br from-[#B76E79] to-[#C5A880] text-white rounded-full flex items-center justify-center text-xs shadow-lg transform group-hover:scale-110 transition-all">
                                                                3
                                                        </div>
                                                </div>
                                        </Link>

                                        {/* Profile with Animated Ring */}
                                        <div className="relative">
                                                <button
                                                        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                        className="group relative w-10 h-10 rounded-full border-2 border-[#B76E79]/30 hover:border-[#B76E79]/50 transition-all"
                                                >
                                                        <img
                                                                src="/user.jpg"
                                                                className="w-full h-full rounded-full object-cover"
                                                                alt="Profile"
                                                        />
                                                        <div className="absolute inset-0 rounded-full border-2 border-[#B76E79] opacity-0 group-hover:opacity-30 animate-pulse-ring" />
                                                </button>

                                                {/* Luxury Dropdown */}
                                                {isDropdownOpen && (
                                                        <div className="absolute right-0 mt-3 w-64 bg-gradient-to-b from-[#FAF6F0] to-[#F5EDE1] backdrop-blur-xl rounded-xl shadow-2xl border border-[#E0D6CC] overflow-hidden">
                                                                <div className="p-4 bg-gradient-to-r from-[#B76E79] to-[#C5A880] text-white">
                                                                        <p className="font-medium">VIP Diamond Member</p>
                                                                        <p className="text-xs opacity-90">12,500 Points</p>
                                                                        <div className="h-1 bg-white/20 rounded-full mt-2">
                                                                                <div className="w-3/4 h-full bg-white rounded-full" />
                                                                        </div>
                                                                </div>
                                                                <div className="space-y-3 p-3">
                                                                        <Link to="/profile" className="flex items-center gap-3 p-2 hover:bg-[#FAF6F0] rounded-lg">
                                                                                <User className="w-5 h-5 text-[#B76E79]" />
                                                                                Account Settings
                                                                        </Link>
                                                                        <button className="flex items-center gap-3 p-2 hover:bg-[#FAF6F0] rounded-lg text-red-500 w-full">
                                                                                <LogOut className="w-5 h-5" />
                                                                                Sign Out
                                                                        </button>
                                                                </div>
                                                        </div>
                                                )}
                                        </div>
                                </div>

                                {/* Mobile Menu Trigger */}
                                <button
                                        className="lg:hidden p-2 hover:bg-[#B76E79]/10 rounded-xl transition-colors z-10"
                                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                >
                                        {isMobileMenuOpen ? (
                                                <X className="w-7 h-7 text-[#6B4E4D] animate-spin-in" />
                                        ) : (
                                                <Menu className="w-7 h-7 text-[#6B4E4D] animate-pulse" />
                                        )}
                                </button>
                        </div>

                        {/* Mobile Menu - Full Screen Luxury Overlay */}
                        {isMobileMenuOpen && (
                                <div className="fixed inset-0 bg-[#FAF6F0]/95 backdrop-blur-2xl z-40 animate-fade-in">
                                        <div className="flex flex-col items-center justify-center h-full space-y-8">
                                                <Link
                                                        to="/"
                                                        className="text-3xl font-medium text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                        onClick={() => setIsMobileMenuOpen(false)}
                                                >
                                                        Home
                                                </Link>
                                                <Link
                                                        to="/products"
                                                        className="text-3xl font-medium text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                        onClick={() => setIsMobileMenuOpen(false)}
                                                >
                                                        Collections
                                                </Link>
                                                <Link
                                                        to="/contact"
                                                        className="text-3xl font-medium text-[#6B4E4D] hover:text-[#B76E79] transition-colors"
                                                        onClick={() => setIsMobileMenuOpen(false)}
                                                >
                                                        Concierge
                                                </Link>
                                        </div>
                                </div>
                        )}
                </nav>
        );
};

export default Navbar;
