import React from "react";
import { useState } from "react";
// import { MapPin, Phone, Mail } from "lucide-react";


const Contact = () => {

        const [name, setName] = useState("");
        const [email, setEmail] = useState("");
        const [message, setMessage] = useState("");


        return (
                <div className="relative min-h-screen bg-[#F5ECE3] flex flex-col items-center px-6 py-16 overflow-hidden">
                        {/* Floating Decorative Elements */}
                        <div className="absolute top-10 left-10 w-24 h-24 bg-[#B88E8D] rounded-full opacity-30"></div>
                        <div className="absolute bottom-16 right-16 w-32 h-32 bg-[#D8B6A4] rounded-full opacity-20"></div>

                        {/* Category Description Section */}
                        <div className="relative z-10 w-full max-w-6xl bg-white shadow-xl rounded-3xl border border-[#D8B6A4] p-8 text-center mb-10">
                                <h2 className="text-3xl font-bold text-[#B88E8D]">{product.category}</h2>
                                <p className="text-gray-700 mt-2">{product.categoryDescription}</p>
                        </div>

                        {/* Product Card */}
                        <div className="relative z-10 w-full max-w-6xl bg-white shadow-2xl rounded-3xl border border-[#D8B6A4] p-10 grid grid-cols-1 md:grid-cols-2 gap-8 items-center">
                                {/* Product Image Section */}
                                <div className="relative flex flex-col items-center">
                                        <span className="absolute top-4 left-4 bg-[#D8B6A4] text-white text-xs font-semibold px-3 py-1 rounded-full shadow-md">Luxury Collection</span>
                                        <div className="bg-gradient-to-b from-[#FAF5F0] to-white shadow-lg rounded-2xl p-6">
                                                <img
                                                        src={product.image}
                                                        alt={product.name}
                                                        className="w-full h-auto object-cover rounded-2xl shadow-md"
                                                />
                                        </div>
                                </div>

                                {/* Product Details Section */}
                                <div className="text-gray-800 space-y-6">
                                        <h2 className="text-4xl font-bold text-[#B88E8D] tracking-wide uppercase">{product.name}</h2>
                                        <p className="text-lg text-gray-700 leading-relaxed border-l-4 border-[#D8B6A4] pl-4">{product.description}</p>
                                        <div className="text-3xl font-extrabold text-[#B88E8D]">${product.price}</div>
                                        <div className="flex space-x-6 mt-6">
                                                <button onClick={handleAddToCart} className="bg-[#B88E8D] text-white py-3 px-8 rounded-lg shadow-lg hover:bg-[#A17A7A] transition duration-300">
                                                        Add to Cart
                                                </button>
                                                <button onClick={handleAddToWishlist} className="border border-[#B88E8D] text-[#B88E8D] py-3 px-8 rounded-lg shadow-md hover:bg-[#B88E8D] hover:text-white transition duration-300">
                                                        Add to Wishlist
                                                </button>
                                        </div>
                                        {message && <p className="text-green-600 font-medium mt-2">{message}</p>}
                                </div>
                        </div>

                        {/* Reviews Section */}
                        <div className="relative z-10 w-full max-w-6xl bg-white shadow-xl rounded-3xl border border-[#D8B6A4] p-8 mt-10">
                                <h3 className="text-3xl font-bold text-[#B88E8D] mb-6">Customer Reviews</h3>
                                {product.reviews && product.reviews.length > 0 ? (
                                        <div className="space-y-4">
                                                {product.reviews.map((review, index) => (
                                                        <div key={index} className="border-b border-[#D8B6A4] pb-4">
                                                                <p className="text-lg font-semibold text-gray-800">{review.reviewer}</p>
                                                                <p className="text-gray-600 italic">"{review.comment}"</p>
                                                        </div>
                                                ))}
                                        </div>
                                ) : (
                                        <p className="text-gray-600">No reviews yet. Be the first to leave a review!</p>
                                )}
                        </div>

                        
                </div>


        );
};
export default Contact;
