import React, { useState, useEffect } from "react"; // ✅ Ensure useEffect is included
import { Link, useNavigate } from "react-router-dom";
import { Menu, X } from "lucide-react"; // Icons


const Navbar = () => {
        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isOpen, setIsOpen] = useState(false);
        const navigate = useNavigate();


        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token); // Convert to boolean
                };

                checkAuth();
                window.addEventListener("storage", checkAuth); // Update when login/logout happens

                return () => window.removeEventListener("storage", checkAuth);
        }, []);


        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/login");
        };


        return (
                <nav className="bg-white shadow-md">
                        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                                <div className="flex justify-between h-16 items-center">
                                        {/* Logo */}
                                        <Link to="/" className="text-2xl font-bold text-gray-800">
                                                Shop<span className="text-blue-600">Ease</span>
                                        </Link>

                                        {/* Desktop Links */}
                                        <div className="hidden md:flex space-x-6">
                                                <Link className="text-gray-600 hover:text-blue-600" to="/">Home</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/contact">Contact</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/products">Products</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/cart">Cart</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/wishlist">Wishlist</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/order-history">Orders</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/profile">Profile</Link>


                                        </div>

                                        {/* Login & Signup Buttons */}
                                        <div className="hidden md:flex space-x-4">
                                                <Link to="/login" className="px-4 py-2 bg-gray-200 rounded-md hover:bg-gray-300">
                                                        Login
                                                </Link>
                                                <Link to="/signup" className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                                                        Signup
                                                </Link>
                                                <button onClick={handleLogout} className="px-4 py-2 bg-red-600 text-white rounded-md">
                                                        Logout
                                                </button>
                                        </div>

                                        {/* Mobile Menu Button */}
                                        <button className="md:hidden" onClick={() => setIsOpen(!isOpen)}>
                                                {isOpen ? <X size={28} /> : <Menu size={28} />}
                                        </button>
                                </div>

                                {/* Mobile Menu */}
                                {isOpen && (
                                        <div className="md:hidden flex flex-col bg-white shadow-lg p-4 space-y-3">
                                                <Link className="text-gray-600 hover:text-blue-600" to="/">Home</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/contact">Contact</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/products">Products</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/cart">Cart</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/wishlist">Wishlist</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/order-history">Orders</Link>
                                                <Link className="text-gray-600 hover:text-blue-600" to="/profile">Profile</Link>
                                                <Link className="px-4 py-2 bg-gray-200 rounded-md hover:bg-gray-300" to="/login">
                                                        Login
                                                </Link>
                                                <Link className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700" to="/signup">
                                                        Signup
                                                </Link>
                                        </div>
                                )}
                        </div>
                </nav>
        );
};

export default Navbar;
