import React, { useState, useEffect } from "react";

const Cart = () => {
        const [cartItems, setCartItems] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        const userId = localStorage.getItem("userId"); // Get user ID
        const token = localStorage.getItem("userToken"); // Get token

        useEffect(() => {
                if (!userId || !token) {
                        setError("Please log in to view your cart.");
                        setLoading(false);
                        return;
                }

                const fetchCart = async () => {
                        console.log("üì° Fetching cart for user:", userId); // ‚úÖ Log user ID
                        console.log("üîë Sending token:", token); // ‚úÖ Log token

                        try {
                                const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
                                        method: "GET",
                                        headers: {
                                                "Content-Type": "application/json",
                                                Authorization: `Bearer ${token}`,
                                        },
                                });

                                console.log("üü¢ Response status:", response.status); // ‚úÖ Log response status
                                const data = await response.json();
                                console.log("üì¶ Response data:", data); // ‚úÖ Log response body

                                if (response.ok) {
                                        setCartItems(data.products || []);
                                } else {
                                        setError(data.message || "Failed to load cart.");
                                }
                        } catch (err) {
                                console.error("‚ùå Fetch error:", err);
                                setError("Server error while fetching cart.");
                        } finally {
                                setLoading(false);
                        }
                };

                fetchCart();
        }, [userId, token]);

        if (loading) return <div>Loading...</div>;
        if (error) return <div className="text-red-600">{error}</div>;

        return (
                <div>
                        <h1>Your Cart</h1>
                        {cartItems.length > 0 ? (
                                cartItems.map((item) => (
                                        <div key={item.product._id}>
                                                <p>{item.product.name} - ${item.product.price} (x{item.quantity})</p>
                                        </div>
                                ))
                        ) : (
                                <p>Your cart is empty.</p>
                        )}
                </div>
        );
};

export default Cart;
