import React, { useState } from "react";
import { Truck, CheckCircle, XCircle, RefreshCw } from "lucide-react";

const OrderHistory = () => {
  const [orders, setOrders] = useState([
    {
      id: 101,
      product: "Gold Pendant Necklace",
      price: 249,
      status: "Pending", // Pending | Shipped | Delivered | Cancelled
      image: "https://via.placeholder.com/150",
    },
    {
      id: 102,
      product: "Diamond Stud Earrings",
      price: 399,
      status: "Shipped",
      image: "https://via.placeholder.com/150",
    },
    {
      id: 103,
      product: "Silver Bracelet",
      price: 199,
      status: "Delivered",
      image: "https://via.placeholder.com/150",
    },
  ]);

  const cancelOrder = (orderId) => {
    setOrders(
      orders.map((order) =>
        order.id === orderId ? { ...order, status: "Cancelled" } : order
      )
    );
  };

  const returnOrder = (orderId) => {
    setOrders(
      orders.map((order) =>
        order.id === orderId ? { ...order, status: "Returned" } : order
      )
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8 flex justify-center">
      <div className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-xl">
        
        {/* Page Header */}
        <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
          Order History ðŸ“¦
        </h1>

        {/* Orders List */}
        {orders.length > 0 ? (
          <div className="space-y-6">
            {orders.map((order) => (
              <div
                key={order.id}
                className="flex items-center bg-gray-50 p-5 rounded-lg shadow-md transition hover:shadow-xl border border-gray-200"
              >
                {/* Product Image */}
                <img
                  src={order.image}
                  alt={order.product}
                  className="w-20 h-20 object-cover rounded-lg mr-6"
                />

                {/* Order Info */}
                <div className="flex-1">
                  <h2 className="text-lg font-semibold text-gray-800">
                    {order.product}
                  </h2>
                  <p className="text-gray-600">${order.price.toFixed(2)}</p>
                  <p
                    className={`font-semibold mt-1 ${
                      order.status === "Pending"
                        ? "text-yellow-500"
                        : order.status === "Shipped"
                        ? "text-blue-500"
                        : order.status === "Delivered"
                        ? "text-green-500"
                        : "text-red-500"
                    }`}
                  >
                    {order.status}
                  </p>
                </div>

                {/* Actions */}
                {order.status === "Pending" && (
                  <button
                    onClick={() => cancelOrder(order.id)}
                    className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                  >
                    <XCircle className="w-5 h-5 mr-1" />
                    Cancel Order
                  </button>
                )}

                {order.status === "Delivered" && (
                  <button
                    onClick={() => returnOrder(order.id)}
                    className="flex items-center bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-900 transition"
                  >
                    <RefreshCw className="w-5 h-5 mr-1" />
                    Return Order
                  </button>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-lg text-gray-500">No orders found.</p>
            <button className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition">
              Continue Shopping
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderHistory;
