import React, { useEffect, useState } from "react";
import axios from "axios";
import { XCircle, RefreshCw, Star, ClipboardList } from "lucide-react";


const Profile = () => {

        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        // const navigate = useNavigate();


        // orders
        const [orders, setOrders] = useState([]);

        useEffect(() => {
                const fetchOrders = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        setError("User not logged in");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/orders/my-orders", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                console.log("✅ Orders Fetched:", data);
                                setOrders(data);
                        } catch (error) {
                                console.error("❌ Error Fetching Orders:", error.response?.data || error.message);
                                setError("Failed to load orders.");
                        } finally {
                                setLoading(false);
                        }
                };

                fetchOrders();
        }, []);

        const cancelOrder = async (orderId) => {
                setOrders(
                        orders.map((order) =>
                                order._id === orderId ? { ...order, orderStatus: "Cancelled" } : order
                        )
                );
                alert("Order cancelled successfully!");
        };

        const returnOrder = async (orderId) => {
                setOrders(
                        orders.map((order) =>
                                order._id === orderId ? { ...order, orderStatus: "Returned" } : order
                        )
                );
                alert("Order return requested!");
        };

        return (
                <div className="relative min-h-screen bg-[#f7f3ee] flex justify-center items-center p-8">

                        {/* Floating Decorative Elements */}
                        <div className="absolute top-16 left-10 w-32 h-32 bg-[#B88E8D] rounded-full opacity-25"></div>
                        <div className="absolute bottom-20 right-16 w-40 h-40 bg-[#D8B6A4] rounded-full opacity-20"></div>

                        {/* Order History Container */}
                        <div className="w-full max-w-5xl bg-white mt-[40px] pt-[20px] p-8 rounded-3xl shadow-2xl relative z-10">

                                {/* Page Header */}
                                <div className="flex items-center justify-start gap-3 mt-[50px] mb-8 text-[#6B4E4D]">
                                        <ClipboardList className="w-8 h-8" />
                                        <h1 className="text-3xl font-bold">Order History</h1>
                                </div>

                                {/* Loading State */}
                                {loading ? (
                                        <p className="text-center text-gray-600 text-lg">Loading orders...</p>
                                ) : error ? (
                                        <p className="text-center text-red-500">{error}</p>
                                ) : orders.length > 0 ? (
                                        <div className="grid gap-6 md:grid-cols-2">
                                                {orders.map((order) => (
                                                        <div
                                                                key={order._id}
                                                                className="flex flex-col bg-[#FFF9F6] p-6 rounded-xl shadow-lg transition border border-[#E0C7B4] hover:shadow-xl"
                                                        >
                                                                {/* Order Details */}
                                                                <div className="flex items-center gap-5">
                                                                        {/* Product Images Stacked */}
                                                                        <div className="relative w-24 h-24">
                                                                                {order.items.map((item, index) => (
                                                                                        <img
                                                                                                key={index}
                                                                                                src="https://via.placeholder.com/100"
                                                                                                alt="Product"
                                                                                                className={`absolute w-20 h-20 object-cover rounded-lg border border-[#D8B6A4] 
                                                                                                        transition-transform duration-300 ease-in-out`}
                                                                                                style={{
                                                                                                        top: `${index * 5}px`,
                                                                                                        left: `${index * 5}px`,
                                                                                                        zIndex: order.items.length - index, // Higher index means above others
                                                                                                        transform: `rotate(${index % 2 === 0 ? "-5deg" : "5deg"})`,
                                                                                                }}
                                                                                        />
                                                                                ))}
                                                                        </div>

                                                                        {/* Order Information */}
                                                                        <div>
                                                                                <h2 className="text-lg font-semibold text-[#6B4E4D]">
                                                                                        {order.items.map((item) => item.name).join(", ")}
                                                                                </h2>
                                                                                <p className="text-gray-600">₹{order.totalAmount.toFixed(2)}</p>
                                                                                <p className={`font-semibold mt-1 
                                                                                                ${order.orderStatus === "Pending" ? "text-yellow-500"
                                                                                                : order.orderStatus === "Confirmed" ? "text-green-500"
                                                                                                        : "text-red-500"
                                                                                        }`}>
                                                                                        {order.orderStatus}
                                                                                </p>
                                                                        </div>
                                                                </div>

                                                                {/* Action Buttons */}
                                                                <div className="flex justify-end mt-4">
                                                                        {order.orderStatus === "Pending" && (
                                                                                <button
                                                                                        onClick={() => cancelOrder(order._id)}
                                                                                        className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                                                                                >
                                                                                        <XCircle className="w-5 h-5 mr-1" />
                                                                                        Cancel Order
                                                                                </button>
                                                                        )}

                                                                        {order.orderStatus === "Confirmed" && (

                                                                                <>
                                                                                        <button
                                                                                                onClick={() => returnOrder(order._id)}
                                                                                                className="flex items-center bg-gray-700 text-white px-4 py-2 rounded-lg hover:bg-gray-900 transition"
                                                                                        >
                                                                                                <RefreshCw className="w-5 h-5 mr-1" />
                                                                                                Return Order
                                                                                        </button>

                                                                                        <button
                                                                                                onClick={() => navigate(`/review`, { state: { order } })}
                                                                                                className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition ml-3"
                                                                                        >
                                                                                                <Star className="w-5 h-5 mr-1" />
                                                                                                Write Review
                                                                                        </button>
                                                                                </>
                                                                        )}
                                                                </div>
                                                        </div>
                                                ))}
                                        </div>
                                ) : (
                                        <div className="text-center py-12">
                                                <p className="text-lg text-gray-500">No orders found.</p>
                                        </div>
                                )}
                        </div>



                </div>
        );
};

export default Profile;
