import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError("Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);

        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
    <div className="container mx-auto p-8 bg-gray-50">
        {product && (
            <div>
                {/* Product Info Section */}
                <div className="flex flex-col lg:flex-row items-center lg:items-start space-y-6 lg:space-y-0 lg:space-x-16 my-12">
                    <div className="flex-1 max-w-xl mx-auto lg:max-w-lg">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-auto object-cover rounded-xl shadow-lg"
                        />
                    </div>
                    <div className="flex-1 pl-6 space-y-6">
                        <h2 className="text-4xl font-semibold text-gray-900">{product.name}</h2>
                        <p className="text-lg text-gray-600">{product.description}</p>
                        <div className="mt-6 flex items-center space-x-8">
                            <span className="text-3xl font-bold text-blue-600">${product.price}</span>
                            <button className="mt-4 bg-blue-600 text-white py-3 px-10 rounded-xl hover:bg-blue-700 transition duration-300 focus:outline-none">
                                Add to Cart
                            </button>
                        </div>
                    </div>
                </div>

                {/* Category Description Section */}
                <div className="my-16 text-center px-4">
                    <h3 className="text-3xl font-semibold text-gray-800">Product Category</h3>
                    <p className="mt-6 text-lg text-gray-700 max-w-4xl mx-auto">
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec ac risus eu risus egestas feugiat.
                        Quisque vestibulum arcu nec velit volutpat, a faucibus enim fringilla. Curabitur et dolor erat.
                        Nunc suscipit nisl ut ante volutpat, nec elementum ante bibendum.
                    </p>
                </div>

                {/* Related Products Section */}
                <div className="my-16">
                    <h3 className="text-3xl font-semibold text-center text-gray-800 mb-8">You May Also Like</h3>
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
                        {/* Product Card 1 */}
                        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
                            <img
                                src="https://via.placeholder.com/300x300"
                                alt="Related Product"
                                className="w-full h-56 object-cover rounded-lg mb-4"
                            />
                            <h4 className="text-xl font-semibold text-gray-800">Related Product 1</h4>
                            <p className="text-lg text-gray-600 mt-2">$29.99</p>
                            <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>

                        {/* Product Card 2 */}
                        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
                            <img
                                src="https://via.placeholder.com/300x300"
                                alt="Related Product"
                                className="w-full h-56 object-cover rounded-lg mb-4"
                            />
                            <h4 className="text-xl font-semibold text-gray-800">Related Product 2</h4>
                            <p className="text-lg text-gray-600 mt-2">$49.99</p>
                            <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>

                        {/* Product Card 3 */}
                        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
                            <img
                                src="https://via.placeholder.com/300x300"
                                alt="Related Product"
                                className="w-full h-56 object-cover rounded-lg mb-4"
                            />
                            <h4 className="text-xl font-semibold text-gray-800">Related Product 3</h4>
                            <p className="text-lg text-gray-600 mt-2">$19.99</p>
                            <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>

                        {/* Product Card 4 */}
                        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
                            <img
                                src="https://via.placeholder.com/300x300"
                                alt="Related Product"
                                className="w-full h-56 object-cover rounded-lg mb-4"
                            />
                            <h4 className="text-xl font-semibold text-gray-800">Related Product 4</h4>
                            <p className="text-lg text-gray-600 mt-2">$39.99</p>
                            <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300">
                                Add to Cart
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        )}
    </div>
);

};

export default SingleProduct;
