import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
    const [wishlist, setWishlist] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const fetchWishlist = async () => {
            try {
                const token = localStorage.getItem("userToken");
                if (!token) {
                    alert("Please log in to view your wishlist.");
                    return;
                }

                const { data } = await axios.get("http://localhost:5000/api/wishlist", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setWishlist(data.wishlist?.products || []);
            } catch (error) {
                setMessage(error.response?.data?.message || "Failed to fetch wishlist.");
            } finally {
                setLoading(false);
            }
        };
        fetchWishlist();
    }, []);

    const removeFromWishlist = async (productId) => {
        try {
            const token = localStorage.getItem("userToken");
            await axios.delete(`http://localhost:5000/api/wishlist/remove/${productId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setWishlist(wishlist.filter((item) => item._id !== productId));
        } catch (error) {
            setMessage(error.response?.data?.message || "Failed to remove item from wishlist.");
        }
    };

    if (loading) return <div>Loading wishlist...</div>;

    return (
        <div className="container mx-auto px-6 py-16">
            <h1 className="text-3xl font-semibold text-gray-800 mb-6">Your Wishlist</h1>
            {wishlist.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                    {wishlist.map((item) => (
                        <div key={item._id} className="bg-white rounded-lg shadow-md p-4">
                            <img
                                src={item.image || "https://via.placeholder.com/150"}
                                alt={item.name}
                                className="w-full h-48 object-cover rounded-t-lg"
                            />
                            <div className="p-4">
                                <h4 className="text-xl font-medium text-gray-800">{item.name}</h4>
                                <p className="text-gray-600 mt-2">${item.price?.toFixed(2)}</p>
                                <div className="flex justify-between mt-4">
                                    <button
                                        onClick={() => navigate(`/product/${item._id}`)}
                                        className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                                    >
                                        View Product
                                    </button>
                                    <button
                                        onClick={() => removeFromWishlist(item._id)}
                                        className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
                                    >
                                        Remove
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Your wishlist is empty.</p>
            )}
            {message && <p className="text-red-600 mt-4">{message}</p>}
        </div>
    );
};

export default Wishlist;
