import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { MapPin, Phone, Edit, LogOut } from "lucide-react";


const Profile = () => {
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;
        if (error) return <div className="text-center text-red-500">{error}</div>;

        return (
                <div className="flex justify-center items-center min-h-screen bg-gray-100 px-4">
                        <div className="w-full max-w-md bg-white shadow-lg rounded-xl overflow-hidden p-6">
                                {/* Profile Header */}
                                <div className="flex flex-col items-center">
                                        <img
                                                className="w-24 h-24 rounded-full border-4 border-blue-500 shadow-md"
                                                src={`https://ui-avatars.com/api/?name=${user?.name}&background=0D8ABC&color=fff`}
                                                alt="User Avatar"
                                        />
                                        <h1 className="text-2xl font-bold text-gray-900 mt-4">{user?.name}</h1>
                                        <p className="text-gray-600">{user?.email}</p>
                                </div>

                                {/* Profile Details */}
                                <div className="mt-6 p-4 border rounded-lg bg-gray-50 shadow-sm space-y-3">
                                        <div className="flex items-center gap-3 text-gray-700">
                                                <Phone className="text-blue-500 w-5 h-5" />
                                                <p><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                        </div>
                                        <div className="flex items-center gap-3 text-gray-700">
                                                <MapPin className="text-blue-500 w-5 h-5" />
                                                <p><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                        </div>
                                </div>

                                {/* Actions */}
                                <div className="mt-6 flex flex-col gap-3">
                                        <button
                                                onClick={() => navigate("/edit-profile")}
                                                className="flex items-center justify-center gap-2 w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition"
                                        >
                                                <Edit className="w-5 h-5" /> Edit Profile
                                        </button>
                                        <button
                                                onClick={handleLogout}
                                                className="flex items-center justify-center gap-2 w-full bg-red-500 text-white py-2 px-4 rounded-lg hover:bg-red-600 transition"
                                        >
                                                <LogOut className="w-5 h-5" /> Logout
                                        </button>
                                </div>
                        </div>
                        </div>
                        );
};

                        export default Profile;
