import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const RecommendedProducts = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState(""); // For feedback messages

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/products");
                console.log("API Response:", response.data);

                let productsArray = response.data; // Directly use response if it's already an array
                if (!Array.isArray(productsArray)) {
                    productsArray = Object.values(response.data); // Convert object to array (if needed)
                }

                console.log("Final Products Array:", productsArray);
                setProducts(productsArray);
            } catch (error) {
                console.error("Error fetching products:", error);
                setProducts([]); // Ensure it's an empty array on failure
            } finally {
                setLoading(false); // Stop loading
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = async (id) => {
        const userId = localStorage.getItem("userId");
        const token = localStorage.getItem("userToken");

        if (!userId || !token) {
            setMessage("‚ö†Ô∏è Please log in to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/cart/add", {
                userId,
                productId: id,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setMessage("‚úÖ Item added to cart successfully!");
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("‚ùå Server error while adding to cart.");
        }

        setTimeout(() => setMessage(""), 3000);
    };

    const handleAddToWishlist = async (id) => {
        const token = localStorage.getItem("userToken");

        if (!token) {
            setMessage("‚ö†Ô∏è Please log in to add items to your wishlist.");
            return;
        }

        try {
            await axios.post(`http://localhost:5000/api/wishlist/add/${id}`, {}, {
                headers: { Authorization: `Bearer ${token}` },
            });

            setMessage("‚ù§Ô∏è Added to wishlist!");
        } catch (error) {
            console.error("‚ùå Wishlist error:", error.response?.data || error.message);
            setMessage("‚ùå Failed to add to wishlist.");
        }

        setTimeout(() => setMessage(""), 3000);
    };

    if (loading) return <p className="text-center text-gray-500">Loading products...</p>;

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {products.length > 0 ? (
                products.map((product) => (
                    <div
                        key={product._id}
                        className="group relative bg-white rounded-xl shadow-sm hover:shadow-md transition-all duration-300 ease-out"
                    >
                        {/* Image */}
                        <div className="relative aspect-square overflow-hidden">
                            <img
                                src={product.images?.[0] || "/placeholder.jpg"}
                                alt={product.name || "Product Image"}
                                className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-105"
                            />
                        </div>

                        {/* Product Info */}
                        <div className="p-6">
                            <h3 className="text-lg font-semibold text-gray-700">{product.name}</h3>
                            <p className="text-[#B76E79] mt-1">${product.price || "N/A"}</p>

                            {/* Buttons */}
                            <div className="mt-4 flex justify-between items-center">
                                <Link
                                    to={`/product/${product._id}`}
                                    className="text-sm text-blue-500 hover:underline"
                                >
                                    View Details
                                </Link>
                                <div className="flex gap-2">
                                    <button onClick={() => handleAddToCart(product._id)} className="text-sm text-green-600 hover:text-green-800">
                                        üõí Add to Cart
                                    </button>
                                    <button onClick={() => handleAddToWishlist(product._id)} className="text-sm text-red-600 hover:text-red-800">
                                        ‚ù§Ô∏è Wishlist
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))
            ) : (
                <p className="text-center text-gray-500">No products available.</p>
            )}
        </div>
    );
};

export default RecommendedProducts;
