import React, { useEffect, useState } from "react";
import axios from "axios";
import { Heart } from "lucide-react";

const Wishlist = () => {
        const [wishlist, setWishlist] = useState([]);
        const [loading, setLoading] = useState(true);

        // Fetch Wishlist Data from Backend
        useEffect(() => {
                const fetchWishlist = async () => {
                        try {
                                const { data } = await axios.get("/api/wishlist", {
                                        headers: { Authorization: `Bearer ${localStorage.getItem("userToken")}` },
                                });

                                // Ensure wishlist exists before accessing `products`
                                setWishlist(data.wishlist?.products || []);
                        } catch (error) {
                                console.error("Error fetching wishlist:", error.response?.data?.message || error.message);
                        } finally {
                                setLoading(false);
                        }
                };

                fetchWishlist();
        }, []);

        // Remove Item from Wishlist
        const removeFromWishlist = async (productId) => {
                try {
                        await axios.delete(`/api/wishlist/remove/${productId}`, {
                                headers: { Authorization: `Bearer ${localStorage.getItem("userToken")}` },
                        });

                        // Update UI after removing item
                        setWishlist((prevWishlist) => prevWishlist.filter((item) => item._id !== productId));
                } catch (error) {
                        console.error("Error removing from wishlist:", error.response?.data?.message || error.message);
                }
        };

        return (
                <div className="min-h-screen bg-gray-100 p-8 flex justify-center">
                        <div className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-xl">
                                <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">Your Wishlist</h1>

                                {loading ? (
                                        <p className="text-center text-gray-500">Loading wishlist...</p>
                                ) : wishlist.length > 0 ? (
                                        <div className="space-y-6">
                                                {wishlist.map((item) => (
                                                        <div
                                                                key={item._id}
                                                                className="flex items-center bg-gray-50 p-5 rounded-lg shadow-md transition border border-gray-200"
                                                        >
                                                                <img
                                                                        src={item.images?.[0] || "https://via.placeholder.com/150"}
                                                                        alt={item.name}
                                                                        className="w-20 h-20 object-cover rounded-lg mr-6"
                                                                />

                                                                <div className="flex-1">
                                                                        <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                                                                        <p className="text-gray-600">${item.price?.toFixed(2)}</p>
                                                                </div>

                                                                <button
                                                                        onClick={() => removeFromWishlist(item._id)}
                                                                        className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                                                                >
                                                                        <Heart className="w-5 h-5 mr-1" />
                                                                        Remove
                                                                </button>
                                                        </div>
                                                ))}
                                        </div>
                                ) : (
                                        <div className="text-center py-12">
                                                <p className="text-lg text-gray-500">Your wishlist is empty.</p>
                                                <button
                                                        className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition"
                                                        onClick={() => (window.location.href = "/products")}
                                                >
                                                        Continue Shopping
                                                </button>
                                        </div>
                                )}
                        </div>
                </div>
        );
};

export default Wishlist;
