import React from "react";
import { Edit, LogOut, Mail, Phone, MapPin } from "lucide-react";

const Profile = () => {
        const user = {
                name: "Sophia Carter",
                email: "sophia@example.com",
                phone: "+1 (555) 123-4567",
                location: "New York, USA",
                avatar: "https://via.placeholder.com/150", // Replace with actual image URL
        };

        return (
                <div className="min-h-screen bg-gray-100 flex items-center justify-center p-6">
                        <div className="w-full max-w-lg bg-white rounded-2xl shadow-lg p-8">

                                {/* Profile Image */}
                                <div className="flex flex-col items-center">
                                        <img
                                                src={user.avatar}
                                                alt="User Avatar"
                                                className="w-24 h-24 rounded-full border-4 border-gray-300"
                                        />
                                        <h2 className="text-2xl font-semibold text-gray-800 mt-4">
                                                {user.name}
                                        </h2>
                                        <p className="text-gray-500 text-sm">Jewelry Enthusiast ðŸ’Ž</p>
                                </div>

                                {/* User Details */}
                                <div className="mt-6 space-y-4">
                                        <div className="flex items-center space-x-3">
                                                <Mail className="w-5 h-5 text-gray-500" />
                                                <p className="text-gray-700">{user.email}</p>
                                        </div>
                                        <div className="flex items-center space-x-3">
                                                <Phone className="w-5 h-5 text-gray-500" />
                                                <p className="text-gray-700">{user.phone}</p>
                                        </div>
                                        <div className="flex items-center space-x-3">
                                                <MapPin className="w-5 h-5 text-gray-500" />
                                                <p className="text-gray-700">{user.location}</p>
                                        </div>
                                </div>

                                {/* Action Buttons */}
                                <div className="mt-6 flex justify-between">
                                        <button className="flex items-center bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-900 transition">
                                                <Edit className="w-5 h-5 mr-2" />
                                                Edit Profile
                                        </button>
                                        <button className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition">
                                                <LogOut className="w-5 h-5 mr-2" />
                                                Logout
                                        </button>
                                </div>
                        </div>
                </div>
        );
};

export default Profile;
