import React, { useState } from "react";
import { Heart, ShoppingCart } from "lucide-react";

const Wishlist = () => {
        const [wishlist, setWishlist] = useState([
                {
                        id: 1,
                        name: "Gold Pendant Necklace",
                        price: 249,
                        image: "https://via.placeholder.com/150",
                },
                {
                        id: 2,
                        name: "Diamond Stud Earrings",
                        price: 399,
                        image: "https://via.placeholder.com/150",
                },
                {
                        id: 3,
                        name: "Silver Bracelet",
                        price: 199,
                        image: "https://via.placeholder.com/150",
                },
        ]);

        const addToCart = (item) => {
                setWishlist(wishlist.filter((wishItem) => wishItem.id !== item.id));
        };

        return (
                <div className="min-h-screen bg-gray-100 p-8 flex justify-center">
                        <div className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-xl">

                                {/* Wishlist Header */}
                                <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
                                        Your Wishlist 
                                </h1>

                                {/* Wishlist Items */}
                                {wishlist.length > 0 ? (
                                        <div className="space-y-6">
                                                {wishlist.map((item) => (
                                                        <div
                                                                key={item.id}
                                                                className="flex items-center bg-gray-50 p-5 rounded-lg shadow-md transition hover:shadow-xl border border-gray-200"
                                                        >
                                                                {/* Product Image */}
                                                                <img
                                                                        src={item.image}
                                                                        alt={item.name}
                                                                        className="w-20 h-20 object-cover rounded-lg mr-6"
                                                                />

                                                                {/* Product Info */}
                                                                <div className="flex-1">
                                                                        <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                                                                        <p className="text-gray-600">${item.price.toFixed(2)}</p>
                                                                </div>

                                                                {/* Buttons */}
                                                                <button
                                                                        onClick={() => addToCart(item)}
                                                                        className="flex items-center bg-amber-600 text-white px-4 py-2 rounded-lg hover:bg-amber-700 transition mr-2"
                                                                >
                                                                        <ShoppingCart className="w-5 h-5 mr-1" />
                                                                        Add to Cart
                                                                </button>
                                                                <button
                                                                        onClick={() => setWishlist(wishlist.filter((wishItem) => wishItem.id !== item.id))}
                                                                        className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                                                                >
                                                                        <Heart className="w-5 h-5 mr-1" />
                                                                        Remove
                                                                </button>
                                                        </div>
                                                ))}
                                        </div>
                                ) : (
                                        <div className="text-center py-12">
                                                <p className="text-lg text-gray-500">Your wishlist is empty.</p>
                                                <button className="mt-4 bg-amber-600 text-white px-6 py-2 rounded-lg hover:bg-amber-700 transition">
                                                        Continue Shopping
                                                </button>
                                        </div>
                                )}
                        </div>
                </div>
        );
};

export default Wishlist;
