import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const CollectionCard = ({ collection }) => {
  const {
    _id,
    name,
    description,
    image,
    category,
    productCount = 0 // We'll get this from the backend
  } = collection;

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
    >
      <Link to={`/collections/${_id}`} className="block">
        <div className="relative aspect-w-16 aspect-h-9">
          <img
            src={image || '/placeholder-collection.jpg'}
            alt={name}
            className="w-full h-48 object-cover"
            onError={(e) => {
              e.target.src = '/placeholder-collection.jpg';
            }}
          />
          {category && (
            <span className="absolute top-2 right-2 bg-primary-600 text-white px-2 py-1 rounded-full text-sm">
              {category.name}
            </span>
          )}
        </div>
        
        <div className="p-4">
          <h3 className="text-lg font-semibold text-gray-800 mb-2 line-clamp-1">
            {name}
          </h3>
          
          <p className="text-gray-600 text-sm mb-3 line-clamp-2">
            {description}
          </p>
          
          <div className="flex items-center justify-between">
            <span className="text-sm text-gray-500">
              {productCount} {productCount === 1 ? 'Product' : 'Products'}
            </span>
            <span className="text-primary-600 text-sm font-medium">
              View Collection â†’
            </span>
          </div>
        </div>
      </Link>
    </motion.div>
  );
};

export default CollectionCard;
