import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Facebook, Twitter, Instagram, User, ShoppingCart, Package, LogOut, Sun, Moon, Phone, Mail } from "lucide-react";

const Navbar = () => {
        const [isAuthenticated, setIsAuthenticated] = useState(false);
        const [isDropdownOpen, setIsDropdownOpen] = useState(false);

        useEffect(() => {
                if (darkMode) {
                        document.documentElement.classList.add("dark");
                        localStorage.setItem("theme", "dark");
                } else {
                        document.documentElement.classList.remove("dark");
                        localStorage.setItem("theme", "light");
                }
        }, [darkMode]);

        const navigate = useNavigate();

        useEffect(() => {
                const checkAuth = () => {
                        const token = localStorage.getItem("userToken");
                        setIsAuthenticated(!!token);
                };

                checkAuth();
                window.addEventListener("storage", checkAuth);
                return () => window.removeEventListener("storage", checkAuth);
        }, []);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage"));
                navigate("/");
        };

        return (
                <>
                        {/* Upper Navbar */}
                        <div className="bg-gray-100 dark:bg-gray-800 text-sm py-2">
                                <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex justify-between items-center">
                                        <div className="flex items-center space-x-4 text-gray-700 dark:text-gray-300">
                                                <Phone className="w-4 h-4" />
                                                <span>+123 456 7890</span>
                                                <Mail className="w-4 h-4 ml-4" />
                                                <span>support@jewelstore.com</span>
                                        </div>
                                        <div className="flex space-x-4">
                                                <Facebook className="w-4 h-4 cursor-pointer hover:text-blue-600 text-gray-700 dark:text-gray-300" />
                                                <Twitter className="w-4 h-4 cursor-pointer hover:text-blue-400 text-gray-700 dark:text-gray-300" />
                                                <Instagram className="w-4 h-4 cursor-pointer hover:text-pink-500 text-gray-700 dark:text-gray-300" />
                                        </div>
                                </div>
                        </div>

                        {/* Main Navbar */}
                        <nav className="bg-white shadow-md dark:bg-gray-900">
                                <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex justify-between items-center h-16">
                                        {/* Logo and Brand Name */}
                                        <Link to="/" className="text-3xl font-bold text-rose-500 flex items-center">
                                                <User className="w-6 h-6 mr-2" /> JewelStore
                                        </Link>

                                        {/* Navigation Links */}
                                        <div className="hidden md:flex space-x-6 text-gray-800 dark:text-gray-200">
                                                <Link to="/" className="hover:text-rose-500 transition duration-300 ease-in-out transform hover:scale-105">Home</Link>
                                                <Link to="/contact" className="hover:text-rose-500 transition duration-300 ease-in-out transform hover:scale-105">Contact</Link>
                                                <Link to="/products" className="hover:text-rose-500 transition duration-300 ease-in-out transform hover:scale-105">Products</Link>
                                        </div>

                                        {/* User Actions */}
                                        <div className="relative flex items-center space-x-4">
                                                {isAuthenticated ? (
                                                        <>
                                                                <div className="relative">
                                                                        <button
                                                                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                                                                onMouseEnter={() => setIsDropdownOpen(true)}
                                                                                onMouseLeave={() => setIsDropdownOpen(false)}
                                                                                className="relative focus:outline-none"
                                                                        >
                                                                                <User className="w-6 h-6 text-gray-800 dark:text-gray-200 cursor-pointer" />
                                                                        </button>
                                                                        {isDropdownOpen && (
                                                                                <div
                                                                                        className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 shadow-lg rounded-md overflow-hidden z-50 transform scale-95 opacity-0 transition-all duration-300 ease-in-out"
                                                                                        onMouseEnter={() => setIsDropdownOpen(true)}
                                                                                        onMouseLeave={() => setIsDropdownOpen(false)}
                                                                                        style={{ transform: isDropdownOpen ? "scale(1) opacity(1)" : "scale(0.95) opacity(0)", transitionDelay: isDropdownOpen ? "0s" : "0.3s" }}
                                                                                >
                                                                                        <Link to="/profile" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Profile</Link>
                                                                                        <Link to="/wishlist" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700">Wishlist</Link>
                                                                                        <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-red-600 hover:bg-gray-100 dark:hover:bg-gray-700">
                                                                                                <LogOut className="w-4 h-4 inline-block mr-2" /> Logout
                                                                                        </button>
                                                                                </div>
                                                                        )}
                                                                </div>
                                                                <Link to="/cart">
                                                                        <ShoppingCart className="w-6 h-6 text-gray-800 dark:text-gray-200 hover:text-rose-500 transition duration-300 ease-in-out transform hover:scale-105" />
                                                                </Link>
                                                                <Link to="/order-history">
                                                                        <Package className="w-6 h-6 text-gray-800 dark:text-gray-200 hover:text-rose-500 transition duration-300 ease-in-out transform hover:scale-105" />
                                                                </Link>
                                                        </>
                                                ) : (
                                                        <Link to="/login" className="px-4 py-2 bg-rose-500 text-white rounded-md hover:bg-rose-600 transition duration-300 ease-in-out transform hover:scale-105">
                                                                Login
                                                        </Link>
                                                )}
                                        </div>
                                </div>
                        </nav>
                </>
        );
};

export default Navbar;
