import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const Profile = () => {
        const [user, setUser] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchUserProfile = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) return navigate("/login");

                                const { data } = await axios.get("http://localhost:5000/api/users/profile", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });
                                setUser(data);
                        } catch (error) {
                                setError("Failed to fetch user profile.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchUserProfile();
        }, [navigate]);

        const handleLogout = () => {
                localStorage.removeItem("userToken");
                window.dispatchEvent(new Event("storage")); // Update authentication state
                navigate("/");
        };

        if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;
        if (error) return <div className="text-center text-red-500">{error}</div>;

        return (
                <div className="flex justify-center items-center min-h-screen bg-gray-100">
                        <div className="w-full max-w-md bg-white shadow-md rounded-lg overflow-hidden p-6">
                                {/* Profile Header */}
                                <div className="flex flex-col items-center">
                                        <img
                                                className="w-24 h-24 rounded-full border-4 border-blue-500 shadow-sm"
                                                src={`https://ui-avatars.com/api/?name=${user?.name}&background=0D8ABC&color=fff`}
                                                alt="User Avatar"
                                        />
                                        <h1 className="text-2xl font-semibold text-gray-900 mt-4">{user?.name}</h1>
                                        <p className="text-gray-600">{user?.email}</p>
                                </div>

                                {/* Profile Details */}
                                <div className="mt-6">
                                        <div className="p-4 border rounded-lg bg-gray-50">
                                                <p className="text-gray-700"><strong>Phone:</strong> {user?.phone || "Not provided"}</p>
                                                <p className="text-gray-700 mt-2"><strong>Address:</strong> {user?.address || "Not provided"}</p>
                                        </div>
                                </div>

                                {/* Actions */}
                                <div className="mt-6 flex flex-col gap-3">
                                        <button
                                                onClick={() => navigate("/edit-profile")}
                                                className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition"
                                        >
                                                Edit Profile
                                        </button>
                                        <button
                                                onClick={handleLogout}
                                                className="w-full bg-red-500 text-white py-2 px-4 rounded-lg hover:bg-red-600 transition"
                                        >
                                                Logout
                                        </button>
                                </div>
                        </div>
                </div>
        );
};

export default Profile;
