import React, { useState } from "react";

const ProductListing = () => {
  // Sample product data
  const products = [
    { id: 1, name: "Nike Running Shoes", price: "$120", image: "https://via.placeholder.com/150" },
    { id: 2, name: "Adidas Sneakers", price: "$100", image: "https://via.placeholder.com/150" },
    { id: 3, name: "Puma Sports Shoes", price: "$90", image: "https://via.placeholder.com/150" },
    { id: 4, name: "Reebok Trainers", price: "$110", image: "https://via.placeholder.com/150" },
    { id: 5, name: "Jordan Sneakers", price: "$150", image: "https://via.placeholder.com/150" },
  ];

  const [searchQuery, setSearchQuery] = useState("");

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      <aside className="w-1/4 bg-white p-6 shadow-md">
        <h2 className="text-xl font-semibold mb-4">Filters</h2>

        {/* Search Bar */}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Search products..."
            className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* Category Filter */}
        <div className="mb-4">
          <h3 className="text-lg font-medium mb-2">Categories</h3>
          <ul className="space-y-2">
            <li><input type="checkbox" className="mr-2" /> Shoes</li>
            <li><input type="checkbox" className="mr-2" /> Sneakers</li>
            <li><input type="checkbox" className="mr-2" /> Trainers</li>
            <li><input type="checkbox" className="mr-2" /> Running</li>
          </ul>
        </div>

        {/* Price Range Filter */}
        <div>
          <h3 className="text-lg font-medium mb-2">Price Range</h3>
          <input type="range" className="w-full cursor-pointer" min="0" max="200" />
        </div>
      </aside>

      {/* Product Listing Section */}
      <main className="w-3/4 p-6">
        <h1 className="text-3xl font-bold mb-6">Our Products</h1>

        {/* Product Grid */}
        <div className="grid grid-cols-3 gap-6">
          {products
            .filter((product) =>
              product.name.toLowerCase().includes(searchQuery.toLowerCase())
            )
            .map((product) => (
              <div key={product.id} className="bg-white p-4 shadow-md rounded-lg">
                <img src={product.image} alt={product.name} className="w-full h-40 object-cover rounded-md" />
                <h3 className="text-lg font-semibold mt-2">{product.name}</h3>
                <p className="text-blue-600 font-medium">{product.price}</p>
                <button className="mt-3 w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition">
                  Add to Cart
                </button>
              </div>
            ))}
        </div>
      </main>
    </div>
  );
};

export default ProductListing;
