import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
        const [wishlist, setWishlist] = useState([]);
        const [loading, setLoading] = useState(true);
        const [message, setMessage] = useState("");
        const navigate = useNavigate();

        useEffect(() => {
                const fetchWishlist = async () => {
                        try {
                                const token = localStorage.getItem("userToken");
                                if (!token) {
                                        alert("Please log in to view your wishlist.");
                                        return;
                                }

                                const { data } = await axios.get("http://localhost:5000/api/wishlist", {
                                        headers: { Authorization: `Bearer ${token}` },
                                });

                                setWishlist(data.wishlist?.products || []);
                        } catch (error) {
                                setMessage(error.response?.data?.message || "Failed to fetch wishlist.");
                        } finally {
                                setLoading(false);
                        }
                };
                fetchWishlist();
        }, []);

        const removeFromWishlist = async (productId) => {
                try {
                        const token = localStorage.getItem("userToken");
                        await axios.delete(`http://localhost:5000/api/wishlist/remove/${productId}`, {
                                headers: { Authorization: `Bearer ${token}` },
                        });

                        setWishlist(wishlist.filter((item) => item._id !== productId));
                        setMessage("❌ Removed from wishlist.");
                } catch (error) {
                        setMessage(error.response?.data?.message || "Failed to remove item from wishlist.");
                }
                hideMessage(); // Auto-hide message after 3 seconds
        };

        const addToCart = async (productId) => {
                try {
                        const token = localStorage.getItem("userToken");
                        const userId = localStorage.getItem("userId");

                        if (!userId || !productId) {
                                setMessage("⚠️ Missing userId or productId.");
                                console.error("🚨 Missing values:", { userId, productId });
                                hideMessage();
                                return;
                        }

                        console.log("📤 Sending request to add product to cart:", { userId, productId });

                        await axios.post(
                                "http://localhost:5000/api/cart/add",
                                { userId, productId },
                                { headers: { Authorization: `Bearer ${token}` } }
                        );

                        setMessage("✅ Product added to cart successfully!");
                } catch (error) {
                        setMessage(error.response?.data?.message || "❌ Failed to add product to cart.");
                        console.error("❌ Error adding to cart:", error);
                }
                hideMessage(); // Auto-hide message after 3 seconds
        };

        // Function to auto-hide message after 3 seconds
        const hideMessage = () => {
                setTimeout(() => {
                        setMessage("");
                }, 3000);
        };



        if (loading) return <div className="text-center text-gray-600 py-10">Loading wishlist...</div>;

        return (
                <div className="container mx-auto px-8 py-16 bg-[#F5E8DD]">
                        <h1 className="text-4xl font-extrabold text-center text-[#6D4C41] mb-12">
                                Your Wishlist
                        </h1>

                        {wishlist.length > 0 ? (
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
                                        {wishlist.map((item) => (
                                                <div
                                                        key={item._id}
                                                        className="bg-[#FFF7F1] rounded-xl shadow-md hover:shadow-xl transition-all duration-300 transform hover:scale-105 p-6 relative"
                                                >
                                                        {/* Product Image */}
                                                        <img
                                                                src={item.image || "https://via.placeholder.com/150"}
                                                                alt={item.name}
                                                                className="w-full h-56 object-cover rounded-xl"
                                                        />

                                                        {/* Product Details */}
                                                        <div className="p-5">
                                                                <h4 className="text-2xl font-semibold text-[#6D4C41]">{item.name}</h4>
                                                                <p className="text-lg text-[#8B5E3C] mt-2 font-medium">${item.price?.toFixed(2)}</p>

                                                                {/* Action Buttons */}
                                                                <div className="flex space-x-3 mt-5">
                                                                        <button
                                                                                onClick={() => addToCart(item._id)}
                                                                                className="w-1/2 bg-[#CFA38A] text-white font-medium px-5 py-2 rounded-lg hover:bg-[#B98266] transition-all duration-300"
                                                                        >
                                                                                Add to Cart
                                                                        </button>
                                                                        <button
                                                                                onClick={() => removeFromWishlist(item._id)}
                                                                                className="w-1/2 bg-[#A35D47] text-white font-medium px-5 py-2 rounded-lg hover:bg-[#8C4A38] transition-all duration-300"
                                                                        >
                                                                                Remove
                                                                        </button>
                                                                </div>

                                                                {/* View Product Button */}
                                                                <button
                                                                        onClick={() => navigate(`/product/${item._id}`)}
                                                                        className="w-full mt-4 bg-[#D9A078] text-white font-medium px-5 py-2 rounded-lg hover:bg-[#C08160] transition-all duration-300"
                                                                >
                                                                        View Product
                                                                </button>
                                                        </div>
                                                </div>
                                        ))}
                                </div>
                        ) : (
                                <div className="text-center py-20">
                                        <p className="text-2xl text-[#8B5E3C]">Your wishlist is empty.</p>
                                </div>
                        )}

                        {/* Success Message */}
                        {message && <p className="text-center text-[#CFA38A] mt-6">{message}</p>}
                </div>

        );
};

export default Wishlist;
