import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EditProfile = () => {
  const [formData, setFormData] = useState({ name: "", email: "", phone: "", address: "" });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("userToken");
        if (!token) return navigate("/login");

        const { data } = await axios.get("http://localhost:5000/api/users/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setFormData({ name: data.name, email: data.email, phone: data.phone, address: data.address });
      } catch (error) {
        setMessage("Failed to fetch user profile.");
      } finally {
        setLoading(false);
      }
    };
    fetchUserProfile();
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("userToken");

      await axios.put("http://localhost:5000/api/users/profile", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setMessage("✅ Profile updated successfully!");

      // Redirect to profile after 1.5 seconds
      setTimeout(() => {
        setMessage(""); // Clear the message before navigating
        navigate("/profile");
      }, 1500);
    } catch (error) {
      setMessage("❌ Failed to update profile.");
    }

    // Auto-hide the message after 3 seconds
    hideMessage();
  };

  // Function to auto-hide the message after 3 seconds
  const hideMessage = () => {
    setTimeout(() => {
      setMessage("");
    }, 3000);
  };


  if (loading) return <div className="text-center text-xl mt-10">Loading...</div>;

  return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-lg rounded-lg mt-10">
      <h2 className="text-2xl font-semibold text-center mb-4">Edit Profile</h2>
      {message && <p className="text-center text-red-500">{message}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <input
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300"
          name="phone"
          placeholder="Phone"
          value={formData.phone}
          onChange={handleChange}
        />
        <input
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:border-blue-300"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
        />
        <button type="submit" className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600">
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default EditProfile;
