import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const CheckoutPage = () => {
        const location = useLocation();
        const navigate = useNavigate();

        const cartItems = location.state?.cartItems || [];
        const finalPayable = location.state?.finalAmount || 0;

        const [address, setAddress] = useState("");

        // Simulated API call to fetch address from database
        useEffect(() => {
                const fetchAddress = async () => {
                        // Simulate fetching the address (replace with real API call)
                        setTimeout(() => {
                                setAddress("123 Main St, City, Country"); // Simulated address
                        }, 1000);
                };

                fetchAddress();
        }, []);

        useEffect(() => {
                if (!location.state) {
                        navigate("/"); // Redirect if no data
                }
        }, [location.state, navigate]);

        const handleProceed = () => {
                if (!address.trim()) {
                        alert("Please enter a valid shipping address.");
                        return;
                }
                alert(`Proceeding to payment for ₹${finalPayable.toFixed(2)}`);
                // API call or redirect to payment page
        };

        return (
                <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10 border border-gray-200">
                        <h2 className="text-2xl font-bold mb-4 text-gray-800">Checkout</h2>

                        <div className="border p-4 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-lg font-semibold mb-2 text-gray-700">Order Summary</h3>
                                {cartItems.map((item) => (
                                        <div key={item.id} className="flex justify-between border-b py-2 text-gray-600">
                                                <span>{item.name} (x{item.quantity})</span>
                                                <span className="font-medium">₹{(item.price * item.quantity).toFixed(2)}</span>
                                        </div>
                                ))}
                                <div className="flex justify-between font-semibold mt-2 text-gray-900">
                                        <span>Final Payable Amount:</span>
                                        <span>₹{finalPayable.toFixed(2)}</span>
                                </div>
                                <p className="text-sm text-gray-600 mt-1">*Tax & Shipping included</p>
                        </div>

                        <div className="border p-4 rounded-lg mb-6 bg-gray-50">
                                <h3 className="text-lg font-semibold mb-2 text-gray-700">Shipping Address</h3>
                                <textarea
                                        className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                        placeholder="Loading address..."
                                />
                        </div>

                        <button
                                onClick={handleProceed}
                                className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition font-semibold disabled:opacity-50"
                                disabled={cartItems.length === 0 || !address.trim()}
                        >
                                {cartItems.length > 0 ? `Proceed to Payment (₹${finalPayable.toFixed(2)})` : "Cart is Empty"}
                        </button>
                </div>
        );
};

export default CheckoutPage;
