import React from "react";

const SingleProduct = () => {
  // Sample Jewelry Product Data
  const product = {
    id: 1,
    name: "Diamond Stud Earrings",
    price: "$799",
    description: "A timeless pair of diamond stud earrings crafted in 18K white gold for a brilliant sparkle.",
    longDescription:
      "Handcrafted with precision, these diamond earrings feature a high-grade 18K white gold setting. The ethically sourced diamonds are cut to perfection, ensuring unmatched brilliance. Ideal for special occasions and everyday elegance.",
    category: "Earrings",
    material: "18K White Gold",
    gemstone: "Natural Diamonds",
    craftsmanship: "Handcrafted",
    image: "https://via.placeholder.com/400",
    sizes: ["Small", "Medium", "Large"],
  };

  // Recommended Jewelry Products
  const recommendedProducts = [
    { id: 2, name: "Gold Necklace", price: "$999", image: "https://via.placeholder.com/150" },
    { id: 3, name: "Sapphire Ring", price: "$1200", image: "https://via.placeholder.com/150" },
    { id: 4, name: "Diamond Bracelet", price: "$1850", image: "https://via.placeholder.com/150" },
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex justify-center">
      <div className="w-full max-w-6xl bg-white p-6 rounded-lg shadow-xl">
        {/* Product Details */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Product Image */}
          <div className="relative group">
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-auto rounded-lg shadow-lg transition-transform duration-300 transform group-hover:scale-105"
            />
          </div>

          {/* Product Info */}
          <div>
            <h1 className="text-4xl font-semibold text-gray-800">{product.name}</h1>
            <p className="text-2xl text-amber-600 font-semibold mt-2">{product.price}</p>
            <p className="text-gray-700 mt-4">{product.description}</p>

            {/* Material and Craftsmanship Details */}
            <div className="mt-4 space-y-2">
              <p className="text-gray-600">
                <strong>Material:</strong> {product.material}
              </p>
              <p className="text-gray-600">
                <strong>Gemstone:</strong> {product.gemstone}
              </p>
              <p className="text-gray-600">
                <strong>Craftsmanship:</strong> {product.craftsmanship}
              </p>
            </div>

            {/* Size Selection */}
            <div className="mt-6">
              <h3 className="font-medium text-lg">Select Size:</h3>
              <div className="flex space-x-3 mt-2">
                {product.sizes.map((size) => (
                  <button
                    key={size}
                    className="border px-4 py-2 rounded-md hover:bg-gray-200 transition text-gray-800 font-medium"
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            {/* Action Buttons */}
            <div className="mt-6 flex space-x-4">
              <button className="bg-amber-600 text-white px-6 py-2 rounded-md shadow hover:bg-amber-700 transition">
                Add to Cart
              </button>
              <button className="bg-gray-900 text-white px-6 py-2 rounded-md shadow hover:bg-gray-800 transition">
                Buy Now
              </button>
            </div>

            {/* Product Category */}
            <p className="mt-6 text-gray-500">
              <strong>Category:</strong> {product.category}
            </p>
          </div>
        </div>

        {/* Long Description */}
        <div className="mt-12">
          <h2 className="text-2xl font-semibold text-gray-800">Product Details</h2>
          <p className="text-gray-600 mt-2 leading-relaxed">{product.longDescription}</p>
        </div>

        {/* Recommended Jewelry */}
        <div className="mt-12">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">You May Also Like</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {recommendedProducts.map((rec) => (
              <div key={rec.id} className="bg-white p-4 rounded-lg shadow-lg transition-transform transform hover:scale-105">
                <img src={rec.image} alt={rec.name} className="w-full h-40 object-cover rounded-md" />
                <h3 className="text-lg font-semibold mt-3 text-gray-800">{rec.name}</h3>
                <p className="text-amber-600 font-medium">{rec.price}</p>
                <button className="mt-3 w-full bg-gray-900 text-white py-2 rounded-md hover:bg-gray-800 transition">
                  View Product
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
