import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const SingleProduct = () => {
        const { id } = useParams(); // Extract product ID from URL
        const [product, setProduct] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);

        // Fetch product data from the backend using fetch
        useEffect(() => {
                const fetchProduct = async () => {
                        console.log("Fetched ID:", id); // Log the ID to ensure it's correct

                        try {
                                const response = await fetch(`http://localhost:5000/api/products/${id}`);
                                const data = await response.json(); // Parse the JSON data

                                console.log("Fetched Product Data:", data); // Log the fetched data

                                if (data) {
                                        setProduct(data);
                                        setLoading(false);
                                } else {
                                        setError("Product not found");
                                        setLoading(false);
                                }
                        } catch (err) {
                                console.error("Error fetching product:", err); // Log any error
                                setError("Product not found or server error");
                                setLoading(false);
                        }
                };

                fetchProduct();
        }, [id]);

        if (loading) return <div className="text-center py-10">Loading...</div>;
        if (error) return <div className="text-center py-10 text-red-600">{error}</div>;

        return (
                <div className="container mx-auto px-6 py-16 bg-gradient-to-r from-blue-100 to-indigo-100">
                    {product && (
                        <div>
                            {/* Product Info Section */}
                            <div className="flex flex-col lg:flex-row items-center lg:items-start space-y-8 lg:space-y-0 lg:space-x-16 my-12">
                                <div className="flex-1 max-w-xl mx-auto lg:max-w-lg">
                                    <img
                                        src={product.image}
                                        alt={product.name}
                                        className="w-full h-full object-cover rounded-xl shadow-xl transition-all duration-500 ease-in-out transform hover:scale-105"
                                    />
                                </div>
                                <div className="flex-1 space-y-6">
                                    <h2 className="text-4xl font-extrabold text-gray-900 hover:text-blue-600 transition duration-300">{product.name}</h2>
                                    <p className="text-lg text-gray-700">{product.description}</p>
                                    <div className="flex items-center space-x-8">
                                        <span className="text-3xl font-bold text-blue-700">${product.price}</span>
                                        <button className="mt-4 bg-blue-600 text-white py-3 px-10 rounded-full hover:bg-blue-700 transition duration-300 transform hover:scale-105">
                                            Add to Cart
                                        </button>
                                    </div>
                                </div>
                            </div>
            
                            {/* Category Description Section */}
                            <div className="my-16 px-8 text-center bg-gradient-to-r from-gray-300 via-gray-100 to-gray-300 rounded-lg p-8 shadow-xl">
                                <h3 className="text-3xl font-bold text-gray-800">Product Category</h3>
                                <p className="mt-6 text-lg text-gray-600 max-w-4xl mx-auto">
                                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus lacinia ex in quam tincidunt, eu efficitur felis
                                    venenatis. Proin gravida quam in est tempus, sit amet dapibus odio malesuada.
                                </p>
                            </div>
            
                            {/* Related Products Section */}
                            <div className="my-16">
                                <h3 className="text-3xl font-semibold text-center text-gray-800 mb-8">You May Also Like</h3>
                                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
                                    {/* Product Card 1 */}
                                    <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 transform hover:scale-105">
                                        <img
                                            src="https://via.placeholder.com/300x300"
                                            alt="Related Product"
                                            className="w-full h-56 object-cover rounded-lg mb-4"
                                        />
                                        <h4 className="text-xl font-semibold text-gray-800 hover:text-blue-600">{product.name}</h4>
                                        <p className="text-lg text-gray-600 mt-2">${product.price}</p>
                                        <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300 transform hover:scale-105">
                                            Add to Cart
                                        </button>
                                    </div>
            
                                    {/* Product Card 2 */}
                                    <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 transform hover:scale-105">
                                        <img
                                            src="https://via.placeholder.com/300x300"
                                            alt="Related Product"
                                            className="w-full h-56 object-cover rounded-lg mb-4"
                                        />
                                        <h4 className="text-xl font-semibold text-gray-800 hover:text-blue-600">Related Product 2</h4>
                                        <p className="text-lg text-gray-600 mt-2">$49.99</p>
                                        <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300 transform hover:scale-105">
                                            Add to Cart
                                        </button>
                                    </div>
            
                                    {/* Product Card 3 */}
                                    <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 transform hover:scale-105">
                                        <img
                                            src="https://via.placeholder.com/300x300"
                                            alt="Related Product"
                                            className="w-full h-56 object-cover rounded-lg mb-4"
                                        />
                                        <h4 className="text-xl font-semibold text-gray-800 hover:text-blue-600">Related Product 3</h4>
                                        <p className="text-lg text-gray-600 mt-2">$19.99</p>
                                        <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300 transform hover:scale-105">
                                            Add to Cart
                                        </button>
                                    </div>
            
                                    {/* Product Card 4 */}
                                    <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 transform hover:scale-105">
                                        <img
                                            src="https://via.placeholder.com/300x300"
                                            alt="Related Product"
                                            className="w-full h-56 object-cover rounded-lg mb-4"
                                        />
                                        <h4 className="text-xl font-semibold text-gray-800 hover:text-blue-600">Related Product 4</h4>
                                        <p className="text-lg text-gray-600 mt-2">$39.99</p>
                                        <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600 transition duration-300 transform hover:scale-105">
                                            Add to Cart
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            );
            
};

export default SingleProduct;
