import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link from React Router
import axios from "axios";

const ProductListing = () => {
        const [products, setProducts] = useState([]);
        const [categories, setCategories] = useState([]); // Fetch categories dynamically
        const [searchQuery, setSearchQuery] = useState("");
        const [selectedCategories, setSelectedCategories] = useState([]);
        const [priceRange, setPriceRange] = useState(20000);
        const [message, setMessage] = useState(""); // State for feedback messages


        useEffect(() => {
                const fetchProducts = async () => {
                        try {
                                const response = await axios.get("http://localhost:5000/api/products");
                                setProducts(response.data);
                        } catch (error) {
                                console.error("Error fetching products:", error);
                        }
                };

                const fetchCategories = async () => {
                        try {
                                const response = await axios.get("http://localhost:5000/api/categories");
                                setCategories(response.data); // Assume backend returns an array of category names
                        } catch (error) {
                                console.error("Error fetching categories:", error);
                        }
                };

                fetchProducts();
                fetchCategories();
        }, []);

        const handleCategoryChange = (category) => {
                setSelectedCategories((prevCategories) =>
                        prevCategories.includes(category)
                                ? prevCategories.filter((c) => c !== category)
                                : [...prevCategories, category]
                );
        };

        const handleAddToCart = async (id) => {
                const userId = localStorage.getItem("userId");
                const token = localStorage.getItem("userToken");

                if (!userId || !token) {
                        setMessage("⚠️ Please log in to add items to the cart.");
                        return;
                }

                try {
                        const response = await fetch("http://localhost:5000/api/cart/add", {
                                method: "POST",
                                headers: {
                                        "Content-Type": "application/json",
                                        Authorization: `Bearer ${token}`,
                                },
                                body: JSON.stringify({ userId, productId: id }),
                        });

                        const data = await response.json();

                        if (response.ok) {
                                setMessage("✅ Item added to cart successfully!");
                        } else {
                                setMessage(`❌ ${data.message || "Error adding to cart"}`);
                        }
                } catch (error) {
                        console.error("Error adding to cart:", error);
                        setMessage("❌ Server error while adding to cart.");
                }

                // Hide message after 3 seconds
                setTimeout(() => setMessage(""), 3000);
        };




        const filteredProducts = products.filter((product) => {
                const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
                const matchesCategory = selectedCategories.length === 0 || selectedCategories.includes(product.category);
                const matchesPrice = product.price <= priceRange;
                return matchesSearch && matchesCategory && matchesPrice;
        });

        return (
                <div className="min-h-screen bg-[#f7f3ee] flex pt-20">
                        {/* Sidebar */}
                        <aside className="w-1/4 bg-white p-8 shadow-xl rounded-2xl border border-gray-300">
                                <h2 className="text-2xl font-bold mb-6 text-[#b76e79] tracking-wide">Filters</h2>

                                {/* Search Bar */}
                                <div className="mb-6">
                                        <input
                                                type="text"
                                                placeholder="Search products..."
                                                className="w-full px-4 py-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-[#b76e79] transition duration-200"
                                                value={searchQuery}
                                                onChange={(e) => setSearchQuery(e.target.value)}
                                        />
                                </div>

                                {/* Categories */}
                                <div className="mb-6">
                                        <h3 className="text-lg font-semibold text-gray-700 mb-3">Categories</h3>
                                        <ul className="space-y-3">
                                                {categories.length > 0 ? (
                                                        categories.map((category) => (
                                                                <li key={category._id} className="flex items-center">
                                                                        <input
                                                                                type="checkbox"
                                                                                id={category.name}
                                                                                className="mr-3 w-5 h-5 text-[#b76e79] border-gray-300 rounded focus:ring-[#b76e79]"
                                                                                checked={selectedCategories.includes(category.name)}
                                                                                onChange={() => handleCategoryChange(category.name)}
                                                                        />
                                                                        <label htmlFor={category.name} className="text-gray-800 cursor-pointer">
                                                                                {category.name}
                                                                        </label>
                                                                </li>
                                                        ))
                                                ) : (
                                                        <p className="text-gray-500">Loading categories...</p>
                                                )}
                                        </ul>
                                </div>

                                {/* Price Range */}
                                <div className="mb-4">
                                        <h3 className="text-lg font-semibold text-gray-700 mb-3">
                                                Max Price: <span className="text-[#b76e79] font-bold">${priceRange}</span>
                                        </h3>
                                        <input
                                                type="range"
                                                className="w-full cursor-pointer appearance-none h-2 bg-gray-300 rounded-lg outline-none accent-[#b76e79]"
                                                min="0"
                                                max="20000"
                                                value={priceRange}
                                                onChange={(e) => setPriceRange(e.target.value)}
                                        />
                                </div>
                        </aside>

                        {/* Main Content */}
                        <main className="w-3/4 bg-white  p-8 shadow-xl rounded-2xl border border-gray-300">
                                {message && (
                                        <div className="mb-4 p-3 text-white text-center bg-green-500 rounded-md">
                                                {message}
                                        </div>
                                )}

                                <h1 className="text-4xl font-extrabold text-[#b76e79] mb-6 tracking-wide">Our Products</h1>
                                <div className="grid grid-cols-3 gap-8">
                                        {filteredProducts.length > 0 ? (
                                                filteredProducts.map((product) => (

                                                        <div key={product.id} className="relative bg-white rounded-2xl shadow-lg border border-[#e0d6cc] overflow-hidden group transition-all duration-500 hover:shadow-2xl hover:-translate-y-3">

                                                                {/* Product Image with 3D Hover Effect */}
                                                                <div className="relative w-full overflow-hidden flex justify-center">
                                                                        <div className="relative w-full h-64 flex justify-center items-center">
                                                                                <img
                                                                                        src={product.image}
                                                                                        alt={product.name}
                                                                                        className="w-full h-64 object-cover transition-all duration-500 transform group-hover:scale-110 group-hover:-translate-y-2 group-hover:rotate-2 group-hover:shadow-xl"
                                                                                />
                                                                                {/* Soft Glow Effect */}
                                                                                <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-all duration-500"></div>
                                                                        </div>

                                                                        {/* Floating Buttons on Hover - FIXED */}
                                                                        <div className="absolute bottom-0 left-0 right-0 flex items-center justify-center space-x-4 opacity-0 group-hover:opacity-100 scale-95 group-hover:scale-100 transition-all duration-500 ease-in-out bg-white/90 backdrop-blur-lg py-3 shadow-md z-20 before:absolute before:inset-0 before:bg-gradient-to-t before:from-white before:to-transparent before:h-full before:w-full before:z-[-1]">
                                                                                <Link to={`/product/${product._id}`} className="px-5 py-2 text-sm font-semibold uppercase tracking-wide text-white bg-[#b76e79] rounded-md shadow-md hover:bg-[#9d5a64] transition">
                                                                                        View
                                                                                </Link>
                                                                                <button onClick={() => handleAddToCart(product._id)} className="px-5 py-2 text-sm font-semibold uppercase tracking-wide text-white bg-amber-500 rounded-md shadow-md hover:bg-amber-600 transition">
                                                                                        Add to Cart
                                                                                </button>
                                                                        </div>


                                                                </div>

                                                                {/* Product Info */}
                                                                <div className="text-center py-5 bg-white relative z-10">
                                                                        <h3 className="text-xl font-semibold text-gray-900">{product.name}</h3>
                                                                        <p className="mt-2 text-[#b76e79] text-lg font-bold">{product.price}</p>
                                                                </div>


                                                        </div>
                                                ))
                                        ) : (
                                                <p className="text-center col-span-3 text-gray-500">Rendering Jewels for you.</p>
                                        )}
                                </div>
                        </main>

                </div>

        );
};

export default ProductListing;
